        };
      }
      requirements.push({
        id: reqId,
        area: area,
        specs: specs
      });
      reqIndex++;
    });
  });

  // If no specific requirements found, create based on detected numbers
  if (requirements.length === 0) {
    if (areas.length >= 2) {
      requirements.push({
        id: 'A',
        area: Math.max(...areas),
        specs: {
          finish: 'Matt',
          coverage: Math.max(...coverages) || 130,
          minDurability: 10
        }
      });
      requirements.push({
        id: 'B',
        area: Math.min(...areas),
        specs: {
          finish: 'Silk',
          coverage: Math.min(...coverages) || 110,
          minDurability: 8,
          laborFee: amounts[0] || 3000
        }
      });
    } else {
      // Fallback requirements
      requirements.push({
        id: 'A',
        area: areas[0] || 25000,
        specs: {
          finish: 'Matt',
          coverage: coverages[0] || 130,
          minDurability: 10
        }
      }, {
