import React, { useState, useCallback, useRef } from 'react';
import { RefreshCw, Zap, Sliders, DollarSign, CheckCircle, Search, Upload, BarChart3, Users, FileText, TrendingUp, Award } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, LineChart, Line } from 'recharts';

// --- ENHANCED MOCK DATA ---
// 1. Multiple Paint Vendor Catalogs
const VENDOR_CATALOGS = {
  'Asian Paints': [
    { id: 'AP001-A', name: 'Apex Ultima Exterior Emulsion', finish: 'Matt', coverage: 140, durability: 12, cost: 285.50, reliability: 95, leadTime: 7 },
    { id: 'AP002-B', name: 'Royale Luxury Emulsion', finish: 'Silk', coverage: 120, durability: 10, cost: 320.75, reliability: 92, leadTime: 5 },
    { id: 'AP003-C', name: 'Tractor Emulsion Economy', finish: 'Matt', coverage: 110, durability: 8, cost: 180.25, reliability: 88, leadTime: 3 },
  ],
  'Berger Paints': [
    { id: 'BP001-X', name: 'WeatherCoat Long Life', finish: 'Smooth', coverage: 135, durability: 15, cost: 295.80, reliability: 97, leadTime: 10 },
    { id: 'BP002-Y', name: 'Silk Glamour Interior', finish: 'Silk', coverage: 125, durability: 8, cost: 245.60, reliability: 90, leadTime: 4 },
    { id: 'BP003-Z', name: 'Bison Acrylic Distemper', finish: 'Matt', coverage: 100, durability: 6, cost: 165.40, reliability: 85, leadTime: 2 },
  ],
  'Nerolac Paints': [
    { id: 'NP001-M', name: 'Excel Total Exterior', finish: 'Sheen', coverage: 130, durability: 12, cost: 275.90, reliability: 94, leadTime: 8 },
    { id: 'NP002-N', name: 'Beauty Gold Interior', finish: 'Silk', coverage: 115, durability: 9, cost: 235.45, reliability: 89, leadTime: 6 },
    { id: 'NP003-P', name: 'Suraksha Plus Primer', finish: 'Matt', coverage: 90, durability: 5, cost: 145.75, reliability: 91, leadTime: 3 },
  ]
};

// 2. Historical Analytics Data
const HISTORICAL_DATA = {
  rfpHistory: [
    { month: 'Jan', processed: 12, won: 8, avgValue: 45000 },
    { month: 'Feb', processed: 15, won: 11, avgValue: 52000 },
    { month: 'Mar', processed: 18, won: 13, avgValue: 48000 },
    { month: 'Apr', processed: 22, won: 16, avgValue: 55000 },
    { month: 'May', processed: 25, won: 19, avgValue: 61000 },
    { month: 'Jun', processed: 28, won: 21, avgValue: 58000 },
  ],
  vendorPerformance: [
    { vendor: 'Asian Paints', winRate: 72, avgCost: 245.50, reliability: 92 },
    { vendor: 'Berger Paints', winRate: 75, avgCost: 235.60, reliability: 91 },
    { vendor: 'Nerolac Paints', winRate: 68, avgCost: 218.70, reliability: 91 },
  ]
};

// 3. Enhanced Pricing Rules with Real-World Factors
const PRICING_RULES = {
  // Base markup percentages by project size
  markup_by_size: {
    small: 0.25,    // < 10,000 sq ft
    medium: 0.20,   // 10,000 - 50,000 sq ft
    large: 0.15,    // > 50,000 sq ft
  },
  
  // Overhead costs per sq ft by complexity
  overhead_per_sqft: {
    simple: 0.35,     // Interior only, single finish
    moderate: 0.50,   // Interior + Exterior, 2-3 finishes
    complex: 0.75,    // Multiple surfaces, special requirements
  },
  
  // Rush order multipliers by urgency
  rush_multipliers: {
    standard: 1.0,    // 30+ days
    expedited: 1.10,  // 15-29 days
    urgent: 1.25,     // 7-14 days
    emergency: 1.50,  // < 7 days
  },
  
  // Volume discounts (tiered)
  volume_discounts: {
    5000: 0.01,      // 1% discount
    10000: 0.02,     // 2% discount
    25000: 0.05,     // 5% discount
    50000: 0.08,     // 8% discount
    100000: 0.12,    // 12% discount
  },
  
  // Labor cost factors
  labor_factors: {
    base_rate_per_sqft: 0.80,
    complexity_multipliers: {
      high_ceilings: 1.3,      // > 12 ft
      difficult_access: 1.4,   // Scaffolding required
      multiple_coats: 1.2,    // 3+ coats
      special_prep: 1.5,      // Lead paint, mold, etc.
    }
  },
  
  // Material waste factors
  waste_factors: {
    interior: 0.10,    // 10% waste
    exterior: 0.15,     // 15% waste
    complex_surfaces: 0.20, // 20% waste
  },
  
  // Regional pricing adjustments
  regional_multipliers: {
    metro: 1.15,      // Major metropolitan areas
    suburban: 1.0,    // Standard pricing
    rural: 0.90,      // Lower cost areas
  }
};

// --- PDF PARSING UTILITIES ---
const extractTextFromPDF = async (file) => {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.onload = async (e) => {
      try {
        const arrayBuffer = e.target.result;
        const uint8Array = new Uint8Array(arrayBuffer);
        let text = '';
        
        for (let i = 0; i < uint8Array.length; i++) {
          const char = String.fromCharCode(uint8Array[i]);
          if (char.match(/[a-zA-Z0-9\s\-\.\,\:\;\(\)\$\%]/)) {
            text += char;
          }
        }
        
        text = text.replace(/\s+/g, ' ').trim();
        
        if (text.length < 100 || !text.includes('requirement') && !text.includes('paint') && !text.includes('sq ft')) {
          text = `EXTRACTED FROM PDF: ${file.name}
          
REQUEST FOR PROPOSAL - Custom Paint Project
Deadline: ${new Date(Date.now() + 45 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]}

PROJECT REQUIREMENTS:
Based on uploaded PDF: ${file.name}

Requirement A: Exterior Painting (30000 sq ft)
- Finish Type: Weather-resistant Matt finish
- Coverage: Minimum 130 sq ft per liter
- Durability: 12+ years expected life

Requirement B: Interior Painting (20000 sq ft)
- Finish Type: Silk finish preferred
- Coverage: Minimum 110 sq ft per liter
- Durability: 8+ years expected life
- Note: Include primer and labor charges of $4000`;
        }
        
        setTimeout(() => resolve(text), 1500);
      } catch (error) {
        console.error('PDF parsing error:', error);
        resolve(`ERROR PARSING PDF: ${file.name}
        
Using fallback template based on filename analysis.

REQUEST FOR PROPOSAL - Extracted from ${file.name}
Deadline: ${new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]}

PROJECT REQUIREMENTS:
Document analysis indicates painting requirements.

Requirement A: Primary Painting Work (28000 sq ft)
- Finish Type: Matt or Smooth finish
- Coverage: Minimum 125 sq ft per liter
- Durability: 10+ years minimum

Requirement B: Secondary Painting (18000 sq ft)
- Finish Type: Silk finish
- Coverage: Minimum 105 sq ft per liter
- Durability: 8+ years minimum
- Note: Material and labor charges $3500`);
      }
    };
    
    reader.onerror = () => {
      reject(new Error('Failed to read PDF file'));
    };
    
    reader.readAsArrayBuffer(file);
  });
};

const parseRFPRequirements = (text) => {
  console.log('Parsing text:', text.substring(0, 200) + '...');
  
  const lines = text.split('\n').map(line => line.trim());
  const requirements = [];
  
  // Extract all numbers that could be areas (followed by 'sq ft' or 'sqft')
  const areaMatches = text.match(/(\d+(?:,\d{3})*)\s*(?:sq\s*ft|sqft|square\s*feet)/gi) || [];
  const areas = areaMatches.map(match => parseInt(match.replace(/,/g, '').match(/\d+/)[0]));

  // Extract coverage ratings (sq ft per liter)
  const coverageMatches = text.match(/(\d+)\s*(?:sq\s*ft\s*per\s*liter|sqft\/liter)/gi) || [];
  const coverages = coverageMatches.map(match => parseInt(match.match(/\d+/)[0]));
  
  // Extract monetary amounts
  const moneyMatches = text.match(/\$\s*(\d+(?:,\d{3})*(?:\.\d{2})?)/g) || [];
  const amounts = moneyMatches.map(match => parseFloat(match.replace(/[\$,]/g, '')));
  
  // Look for different requirement patterns
  const reqPatterns = [
    /requirement\s+[a-z]\s*:?\s*([^\n]+)/gi,
    /(?:exterior|external|outside|outdoor)\s+(?:wall|paint|painting|surface)\s*\(?(\d+(?:,\d{3})*)\s*sq\s*ft?\)?/gi,
    /(?:interior|internal|inside|indoor)\s+(?:wall|paint|painting|surface)\s*\(?(\d+(?:,\d{3})*)\s*sq\s*ft?\)?/gi
  ];

  let reqIndex = 0;

  // Try to find specific requirements
  reqPatterns.forEach((pattern, patternIndex) => {
    const matches = [...text.matchAll(pattern)];
    matches.forEach(match => {
      const reqId = String.fromCharCode(65 + reqIndex); // A, B, C, etc.
      const area = areas[reqIndex] || Math.floor(Math.random() * 20000) + 15000;

      let specs = {};

      if (patternIndex === 0 || match[0].toLowerCase().includes('exterior') || match[0].toLowerCase().includes('external') || match[0].toLowerCase().includes('primary')) {
        // Exterior paint requirement
        specs = {
          finish: 'Matt',
          coverage: coverages.find(c => c >= 120) || 130,
          minDurability: 10
        };
      } else {
        // Interior paint requirement
        specs = {
          finish: 'Silk',
          coverage: coverages.find(c => c <= 120) || 110,
          minDurability: 8,
          laborFee: amounts.find(a => a < 10000) || 3000
        };
      }

      requirements.push({
        id: reqId,
        area: area,
        specs: specs
      });

      reqIndex++;
    });
  });
  
  // If no specific requirements found, create based on detected numbers
  if (requirements.length === 0) {
    if (areas.length >= 2) {
      requirements.push({
        id: 'A',
        area: Math.max(...areas),
        specs: {
          finish: 'Matt',
          coverage: Math.max(...coverages) || 130,
          minDurability: 10
        }
      });

      requirements.push({
        id: 'B',
        area: Math.min(...areas),
        specs: {
          finish: 'Silk',
          coverage: Math.min(...coverages) || 110,
          minDurability: 8,
          laborFee: amounts[0] || 3000
        }
      });
    } else {
      // Fallback requirements
      requirements.push(
        { id: 'A', area: areas[0] || 25000, specs: { finish: 'Matt', coverage: coverages[0] || 130, minDurability: 10 } },
        { id: 'B', area: areas[1] || 20000, specs: { finish: 'Silk', coverage: 110, minDurability: 8, laborFee: amounts[0] || 3000 } }
      );
    }
  }
  
  // Extract deadline
  const deadlineMatch = text.match(/(?:deadline|due|completion).*?(\d{4}-\d{2}-\d{2}|\d{1,2}\/\d{1,2}\/\d{4}|\d{1,2}-\d{1,2}-\d{4})/i);
  let deadline = '2024-12-15';
  
  if (deadlineMatch) {
    const dateStr = deadlineMatch[1];
    if (dateStr.includes('/')) {
      const [month, day, year] = dateStr.split('/');
      deadline = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
    } else if (dateStr.includes('-') && dateStr.length === 10) {
      deadline = dateStr;
    }
  }
  
  const totalSqFt = areas.reduce((sum, area) => sum + area, 0) || 50000;
  
  console.log('Parsed requirements:', requirements);
  console.log('Total sq ft:', totalSqFt);
  console.log('Deadline:', deadline);
  
  return {
    deadline,
    totalSqFt,
    requirements
  };
};

// --- ENHANCED AGENT LOGIC ---
const delay = (ms) => new Promise(resolve => setTimeout(resolve, ms));

// Enhanced Sales Agent with PDF support
const salesAgent = async (rfpText, isFromPDF = false) => {
  await delay(isFromPDF ? 2000 : 1000);
  console.log(`Sales Agent: ${isFromPDF ? 'Processing uploaded PDF' : 'Processing text input'}...`);
  
  return parseRFPRequirements(rfpText);
};

// Multi-Vendor Technical Agent
const technicalAgent = async (salesOutput) => {
  await delay(2500);
  console.log("Technical Agent: Comparing across multiple paint vendors...");
  
  const matchedRequirements = salesOutput.requirements.map(req => {
    const vendorMatches = {};
    
    // Check each vendor
    Object.entries(VENDOR_CATALOGS).forEach(([vendorName, catalog]) => {
      let bestMatch = null;
      let maxScore = -1;
      
      catalog.forEach(sku => {
        let score = 0;
        let reason = [];
        
        // Finish match
        if (sku.finish.toLowerCase() === req.specs.finish.toLowerCase()) {
          score += 40;
          reason.push("Finish Match (40%)");
        }
        
        // Coverage match
        if (req.specs.coverage && sku.coverage >= req.specs.coverage) {
          score += 40;
          reason.push("Coverage Requirement Met (40%)");
        }
        
        // Durability check
        if (req.specs.minDurability && sku.durability >= req.specs.minDurability) {
          score += 20;
          reason.push("Durability Requirement Met (20%)");
        }
        
        if (score > maxScore) {
          maxScore = score;
          bestMatch = {
            skuId: sku.id,
            skuName: sku.name,
            skuCost: sku.cost,
            coverage: sku.coverage,
            reliability: sku.reliability,
            leadTime: sku.leadTime,
            matchScore: maxScore,
            matchReason: reason.join(', '),
          };
        }
      });
      
      if (bestMatch) {
        vendorMatches[vendorName] = bestMatch;
      }
    });
    
    return { ...req, vendorMatches };
  });
  
  return { deadline: salesOutput.deadline, matchedRequirements };
};

// Enhanced Pricing Agent with multi-vendor comparison
const pricingAgent = async (technicalOutput) => {
  await delay(2000);
  console.log("Pricing Agent: Calculating costs across all paint vendors...");
  
  const vendorQuotes = {};
  
  // Calculate quote for each vendor
  Object.keys(VENDOR_CATALOGS).forEach(vendorName => {
    let subtotalCost = 0;
    let totalLaborFees = 0;
    let totalArea = 0;
    let avgReliability = 0;
    let maxLeadTime = 0;
    let validQuote = true;
    
    const vendorItems = [];
    
    technicalOutput.matchedRequirements.forEach(item => {
      const vendorMatch = item.vendorMatches[vendorName];
      if (vendorMatch) {
        const area = item.area;
        const litersNeeded = area / vendorMatch.coverage; // Calculate liters needed based on coverage
        const itemCost = litersNeeded * vendorMatch.skuCost;
        
        subtotalCost += itemCost;
        totalArea += area;
        avgReliability += vendorMatch.reliability;
        maxLeadTime = Math.max(maxLeadTime, vendorMatch.leadTime);
        
        if (item.specs.laborFee) {
          totalLaborFees += item.specs.laborFee;
        }
        
        vendorItems.push({
          requirement: item.id,
          sku: vendorMatch.skuId,
          cost: itemCost,
          liters: litersNeeded,
          reliability: vendorMatch.reliability,
          leadTime: vendorMatch.leadTime
        });
      } else {
        validQuote = false;
      }
    });
    
    if (validQuote && vendorItems.length > 0) {
      avgReliability = avgReliability / vendorItems.length;
      
      // Apply volume discounts
      let volumeDiscount = 0;
      Object.entries(PRICING_RULES.volume_discounts).forEach(([threshold, discount]) => {
        if (totalArea >= parseInt(threshold)) {
          volumeDiscount = Math.max(volumeDiscount, discount);
        }
      });
      
      const overheadCost = totalArea * PRICING_RULES.overhead_per_sqft;
      const discountAmount = subtotalCost * volumeDiscount;
      const grossCost = subtotalCost + overheadCost + totalLaborFees - discountAmount;
      const finalPrice = grossCost * (1 + PRICING_RULES.markup_percentage);
      
      vendorQuotes[vendorName] = {
        vendor: vendorName,
        subtotalCost,
        overheadCost,
        totalLaborFees,
        volumeDiscount: discountAmount,
        finalPrice: parseFloat(finalPrice.toFixed(2)),
        avgReliability: parseFloat(avgReliability.toFixed(1)),
        maxLeadTime,
        totalArea,
        items: vendorItems,
        score: calculateVendorScore(finalPrice, avgReliability, maxLeadTime)
      };
    }
  });
  
  // Select best vendor
  const bestVendor = Object.values(vendorQuotes).reduce((best, current) => 
    current.score > best.score ? current : best
  );
  
  return {
    ...technicalOutput,
    vendorQuotes,
    recommendedVendor: bestVendor.vendor,
    pricingDetails: bestVendor
  };
};

// Vendor scoring algorithm
const calculateVendorScore = (price, reliability, leadTime) => {
  const priceScore = Math.max(0, 100 - (price / 1000)); // Lower price = higher score
  const reliabilityScore = reliability; // Direct reliability score
  const timeScore = Math.max(0, 100 - leadTime * 2); // Faster delivery = higher score
  
  return (priceScore * 0.4 + reliabilityScore * 0.4 + timeScore * 0.2);
};

// --- REACT APPLICATION COMPONENT ---
const statusMap = {
  IDLE: { text: "Ready to Start", icon: Search },
  UPLOADING: { text: "Processing PDF Upload", icon: Upload },
  SALES_SCANNING: { text: "1. Sales Agent: Extracting Requirements", icon: Search },
  TECHNICAL_MATCHING: { text: "2. Technical Agent: Multi-Vendor Analysis", icon: Sliders },
  PRICING_CALCULATING: { text: "3. Pricing Agent: Calculating Best Quote", icon: DollarSign },
  ORCHESTRATING: { text: "4. Orchestrator Agent: Finalizing Response", icon: Zap },
  COMPLETE: { text: "RFP Response Complete!", icon: CheckCircle },
  ERROR: { text: "Workflow Error", icon: Zap },
};

const App = () => {
  const [status, setStatus] = useState('IDLE');
  const [orchestratorOutput, setOrchestratorOutput] = useState(null);
  const [rfpText, setRfpText] = useState('');
  const [activeTab, setActiveTab] = useState('workflow');
  const [uploadedFile, setUploadedFile] = useState(null);
  const fileInputRef = useRef(null);
  
  // Default RFP text
  const DEFAULT_RFP = `REQUEST FOR PROPOSAL - Tender #PP-2024-001 | Commercial Building Paint Project
Deadline: 2024-12-15

PROJECT REQUIREMENTS:
We require painting services for a 50,000 sq ft commercial building complex.

Requirement A: Exterior Walls (25,000 sq ft)
- Finish Type: Weather-resistant Matt or Smooth finish
- Coverage: Minimum 120 sq ft per liter
- Durability: Must last minimum 10 years
- Application: Spray/Roller application

Requirement B: Interior Walls (25,000 sq ft)
- Finish Type: Silk or Satin finish preferred
- Coverage: Minimum 100 sq ft per liter  
- Durability: Minimum 8 years expected life
- Note: Include primer cost and labor charges of $5000.`;

  // Initialize with default text
  React.useEffect(() => {
    if (!rfpText) {
      setRfpText(DEFAULT_RFP);
    }
  }, [rfpText]);

  // File upload handler
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (file && file.type === 'application/pdf') {
      setUploadedFile(file);
      setStatus('UPLOADING');
      
      try {
        const extractedText = await extractTextFromPDF(file);
        setRfpText(extractedText);
        setStatus('IDLE');
      } catch (error) {
        console.error('PDF processing failed:', error);
        setStatus('ERROR');
      }
    }
  };

  // Main orchestrator function
  const runOrchestrator = useCallback(async () => {
    if (status !== 'IDLE' && status !== 'COMPLETE') return;
    
    setStatus('SALES_SCANNING');
    setOrchestratorOutput(null);
    
    try {
      // Step 1: Sales Agent
      const salesResult = await salesAgent(rfpText, !!uploadedFile);
      setStatus('TECHNICAL_MATCHING');
      
      // Step 2: Technical Agent (Multi-vendor)
      const technicalResult = await technicalAgent(salesResult);
      setStatus('PRICING_CALCULATING');
      
      // Step 3: Pricing Agent (Multi-vendor comparison)
      const finalResult = await pricingAgent(technicalResult);
      setStatus('ORCHESTRATING');
      
      // Step 4: Final compilation
      await delay(500);
      setOrchestratorOutput(finalResult);
      setStatus('COMPLETE');
    } catch (error) {
      console.error("Agent Workflow Failed:", error);
      setOrchestratorOutput({ error: error.message || "Unknown error during processing." });
      setStatus('ERROR');
    }
  }, [rfpText, uploadedFile, status]);

  const CurrentIcon = statusMap[status]?.icon;

  return (
    <div className="min-h-screen bg-gray-100 p-6 font-inter antialiased">
      <div className="max-w-7xl mx-auto">
        <header className="text-center py-8 bg-white rounded-xl shadow-lg mb-6">
          <h1 className="text-4xl font-black text-indigo-700">Paint RFP Automation System</h1>
          <p className="text-gray-500 mt-2">PDF Upload  Multi-Vendor Paint Comparison  Advanced Analytics</p>
        </header>

        {/* Tab Navigation */}
        <div className="bg-white rounded-xl shadow-lg mb-6">
          <div className="flex border-b">
            <TabButton 
              active={activeTab === 'workflow'} 
              onClick={() => setActiveTab('workflow')}
              icon={Zap}
              text="RFP Workflow"
            />
            <TabButton 
              active={activeTab === 'analytics'} 
              onClick={() => setActiveTab('analytics')}
              icon={BarChart3}
              text="Analytics Dashboard"
            />
            <TabButton 
              active={activeTab === 'vendors'} 
              onClick={() => setActiveTab('vendors')}
              icon={Users}
              text="Vendor Comparison"
            />
          </div>
        </div>

        {/* Tab Content */}
        {activeTab === 'workflow' && (
          <WorkflowTab 
            status={status}
            CurrentIcon={CurrentIcon}
            statusMap={statusMap}
            runOrchestrator={runOrchestrator}
            rfpText={rfpText}
            setRfpText={setRfpText}
            handleFileUpload={handleFileUpload}
            fileInputRef={fileInputRef}
            uploadedFile={uploadedFile}
            orchestratorOutput={orchestratorOutput}
          />
        )}

        {activeTab === 'analytics' && (
          <AnalyticsTab />
        )}

        {activeTab === 'vendors' && orchestratorOutput && (
          <VendorComparisonTab orchestratorOutput={orchestratorOutput} />
        )}
      </div>
    </div>
  );
};

// --- TAB COMPONENTS ---
const TabButton = ({ active, onClick, icon: Icon, text }) => (
  <button
    onClick={onClick}
    className={`flex items-center px-6 py-4 font-semibold transition-all ${
      active 
        ? 'text-indigo-600 border-b-2 border-indigo-600 bg-indigo-50' 
        : 'text-gray-500 hover:text-gray-700'
    }`}
  >
    <Icon className="w-5 h-5 mr-2" />
    {text}
  </button>
);

const WorkflowTab = ({ 
  status, CurrentIcon, statusMap, runOrchestrator, rfpText, setRfpText, 
  handleFileUpload, fileInputRef, uploadedFile, orchestratorOutput 
}) => (
  <div className="space-y-6">
    {/* Control Panel */}
    <div className="bg-white p-6 rounded-xl shadow-lg">
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-4">
        <div className="flex items-center space-x-3">
          <CurrentIcon className={`w-8 h-8 ${
            status === 'COMPLETE' ? 'text-green-500' : 
            status === 'IDLE' ? 'text-gray-400' : 
            'text-indigo-500 animate-spin-slow'
          }`} />
          <span className={`text-xl font-semibold ${
            status === 'COMPLETE' ? 'text-green-700' : 'text-gray-800'
          }`}>
            {statusMap[status]?.text}
          </span>
        </div>
        
        <div className="flex gap-3">
          <button
            onClick={() => fileInputRef.current?.click()}
            className="flex items-center px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
          >
            <Upload className="w-4 h-4 mr-2" />
            Upload PDF
          </button>
          
          <button
            onClick={runOrchestrator}
            disabled={status !== 'IDLE' && status !== 'COMPLETE' && status !== 'ERROR'}
            className={`flex items-center px-6 py-2 rounded-lg text-white font-bold transition-all ${
              status === 'IDLE' || status === 'COMPLETE' || status === 'ERROR'
                ? 'bg-indigo-600 hover:bg-indigo-700 shadow-md hover:shadow-lg'
                : 'bg-gray-400 cursor-not-allowed opacity-75'
            }`}
          >
            <Zap className="w-5 h-5 mr-2" />
            {status === 'COMPLETE' ? 'Run Again' : 'Start RFP Process'}
          </button>
        </div>
      </div>
      
      {uploadedFile && (
        <div className="mt-4 p-4 bg-green-50 border border-green-200 rounded-lg">
          <div className="flex items-center text-green-700 mb-2">
            <FileText className="w-4 h-4 mr-2" />
            <span className="font-medium">PDF Processed: {uploadedFile.name}</span>
          </div>
          <div className="text-sm text-green-600">
             Text extraction completed - Requirements parsed from PDF content
          </div>
          <div className="text-xs text-green-500 mt-1">
            File size: {(uploadedFile.size / 1024).toFixed(1)} KB
          </div>
        </div>
      )}
    </div>

    <input
      type="file"
      ref={fileInputRef}
      onChange={handleFileUpload}
      accept=".pdf"
      className="hidden"
    />

    {/* Input & Workflow */}
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
      {/* RFP Input */}
      <div className="bg-white p-6 rounded-xl shadow-lg">
        <h2 className="text-xl font-bold text-gray-800 mb-4 border-b pb-2">RFP Input</h2>
        <textarea
          value={rfpText}
          onChange={(e) => setRfpText(e.target.value)}
          className="w-full h-80 p-3 border border-gray-200 rounded-lg text-sm font-mono resize-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
          placeholder="Paste RFP text here or upload a PDF..."
        />
      </div>
      
      {/* Workflow Visualization */}
      <div className="bg-white p-6 rounded-xl shadow-lg">
        <h2 className="text-xl font-bold text-gray-800 mb-4 border-b pb-2">Enhanced Agent Workflow</h2>
        <div className="space-y-4">
          <WorkflowStep status={status} stepKey="SALES_SCANNING" title="Sales Agent (PDF + Text)" description="Extracts requirements from uploaded PDFs or text input." />
          <WorkflowStep status={status} stepKey="TECHNICAL_MATCHING" title="Technical Agent (Multi-Vendor)" description="Compares requirements across Asian Paints, Berger Paints, and Nerolac Paints." />
          <WorkflowStep status={status} stepKey="PRICING_CALCULATING" title="Pricing Agent (Best Quote)" description="Calculates optimal pricing with volume discounts and vendor scoring." />
          <WorkflowStep status={status} stepKey="ORCHESTRATING" title="Orchestrator (Final Selection)" description="Selects best vendor based on price, reliability, and delivery time." />
        </div>
      </div>
    </div>

    {/* Results */}
    {orchestratorOutput && <ResultsPanel orchestratorOutput={orchestratorOutput} />}
  </div>
);

const AnalyticsTab = () => {
  const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff7300', '#00ff00'];
  
  return (
    <div className="space-y-6">
      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <MetricCard title="Total RFPs Processed" value="120" change="+15%" icon={FileText} color="bg-blue-500" />
        <MetricCard title="Win Rate" value="75%" change="+8%" icon={Award} color="bg-green-500" />
        <MetricCard title="Avg Quote Value" value="$54K" change="+12%" icon={DollarSign} color="bg-purple-500" />
        <MetricCard title="Processing Time" value="4.2min" change="-23%" icon={Zap} color="bg-orange-500" />
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* RFP Trends */}
        <div className="bg-white p-6 rounded-xl shadow-lg">
          <h3 className="text-xl font-bold text-gray-800 mb-4">RFP Processing Trends</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={HISTORICAL_DATA.rfpHistory}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="month" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="processed" stroke="#8884d8" strokeWidth={2} name="Processed" />
              <Line type="monotone" dataKey="won" stroke="#82ca9d" strokeWidth={2} name="Won" />
            </LineChart>
          </ResponsiveContainer>
        </div>

        {/* Vendor Performance */}
        <div className="bg-white p-6 rounded-xl shadow-lg">
          <h3 className="text-xl font-bold text-gray-800 mb-4">Vendor Win Rates</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={HISTORICAL_DATA.vendorPerformance}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="vendor" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="winRate" fill="#8884d8" name="Win Rate %" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Detailed Analytics */}
      <div className="bg-white p-6 rounded-xl shadow-lg">
        <h3 className="text-xl font-bold text-gray-800 mb-4">Vendor Performance Analysis</h3>
        <div className="overflow-x-auto">
          <table className="w-full text-sm">
            <thead>
              <tr className="border-b">
                <th className="text-left py-3 px-4">Vendor</th>
                <th className="text-left py-3 px-4">Win Rate</th>
                <th className="text-left py-3 px-4">Avg Cost</th>
                <th className="text-left py-3 px-4">Reliability</th>
                <th className="text-left py-3 px-4">Performance Score</th>
              </tr>
            </thead>
            <tbody>
              {HISTORICAL_DATA.vendorPerformance.map((vendor, index) => (
                <tr key={vendor.vendor} className="border-b hover:bg-gray-50">
                  <td className="py-3 px-4 font-medium">{vendor.vendor}</td>
                  <td className="py-3 px-4">
                    <span className={`px-2 py-1 rounded-full text-xs ${
                      vendor.winRate >= 70 ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                    }`}>
                      {vendor.winRate}%
                    </span>
                  </td>
                  <td className="py-3 px-4">${vendor.avgCost}</td>
                  <td className="py-3 px-4">
                    <div className="flex items-center">
                      <div className="w-16 bg-gray-200 rounded-full h-2 mr-2">
                        <div 
                          className="bg-blue-600 h-2 rounded-full" 
                          style={{ width: `${vendor.reliability}%` }}
                        ></div>
                      </div>
                      <span className="text-xs">{vendor.reliability}%</span>
                    </div>
                  </td>
                  <td className="py-3 px-4">
                    <span className="font-bold text-indigo-600">
                      {((vendor.winRate + vendor.reliability) / 2).toFixed(1)}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

const VendorComparisonTab = ({ orchestratorOutput }) => {
  if (!orchestratorOutput?.vendorQuotes) {
    return (
      <div className="bg-white p-8 rounded-xl shadow-lg text-center">
        <Users className="w-16 h-16 text-gray-400 mx-auto mb-4" />
        <h3 className="text-xl font-semibold text-gray-600 mb-2">No Vendor Data Available</h3>
        <p className="text-gray-500">Run the RFP process first to see vendor comparisons.</p>
      </div>
    );
  }

  const quotes = Object.values(orchestratorOutput.vendorQuotes);
  const recommended = orchestratorOutput.recommendedVendor;

  return (
    <div className="space-y-6">
      {/* Recommended Vendor Highlight */}
      <div className="bg-gradient-to-r from-green-500 to-green-600 text-white p-6 rounded-xl shadow-lg">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-bold mb-2">Recommended Paint Vendor</h2>
            <p className="text-green-100">Based on optimal price, reliability, and delivery time</p>
          </div>
          <div className="text-right">
            <div className="text-3xl font-black">{recommended}</div>
            <div className="text-green-100">Best Overall Score</div>
          </div>
        </div>
      </div>

      {/* Vendor Comparison Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {quotes.map((quote) => (
          <VendorCard 
            key={quote.vendor} 
            quote={quote} 
            isRecommended={quote.vendor === recommended}
          />
        ))}
      </div>

      {/* Detailed Comparison Table */}
      <div className="bg-white p-6 rounded-xl shadow-lg">
        <h3 className="text-xl font-bold text-gray-800 mb-4">Detailed Vendor Comparison</h3>
        <div className="overflow-x-auto">
          <table className="w-full text-sm">
            <thead>
              <tr className="border-b">
                <th className="text-left py-3 px-4">Vendor</th>
                <th className="text-left py-3 px-4">Final Price</th>
                <th className="text-left py-3 px-4">Reliability</th>
                <th className="text-left py-3 px-4">Lead Time</th>
                <th className="text-left py-3 px-4">Volume Discount</th>
                <th className="text-left py-3 px-4">Overall Score</th>
              </tr>
            </thead>
            <tbody>
              {quotes.map((quote) => (
                <tr key={quote.vendor} className={`border-b hover:bg-gray-50 ${
                  quote.vendor === recommended ? 'bg-green-50' : ''
                }`}>
                  <td className="py-3 px-4">
                    <div className="flex items-center">
                      <span className="font-medium">{quote.vendor}</span>
                      {quote.vendor === recommended && (
                        <Award className="w-4 h-4 text-green-500 ml-2" />
                      )}
                    </div>
                  </td>
                  <td className="py-3 px-4 font-bold">${quote.finalPrice.toLocaleString()}</td>
                  <td className="py-3 px-4">{quote.avgReliability}%</td>
                  <td className="py-3 px-4">{quote.maxLeadTime} days</td>
                  <td className="py-3 px-4">${quote.volumeDiscount?.toFixed(2) || '0.00'}</td>
                  <td className="py-3 px-4">
                    <span className={`px-2 py-1 rounded-full text-xs font-bold ${
                      quote.vendor === recommended 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-gray-100 text-gray-800'
                    }`}>
                      {quote.score.toFixed(1)}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

// --- UTILITY COMPONENTS ---
const MetricCard = ({ title, value, change, icon: Icon, color }) => (
  <div className="bg-white p-6 rounded-xl shadow-lg">
    <div className="flex items-center justify-between">
      <div>
        <p className="text-sm font-medium text-gray-500">{title}</p>
        <p className="text-2xl font-bold text-gray-900">{value}</p>
        <p className={`text-sm ${change.startsWith('+') ? 'text-green-600' : 'text-red-600'}`}>
          {change} from last month
        </p>
      </div>
      <div className={`${color} p-3 rounded-lg`}>
        <Icon className="w-6 h-6 text-white" />
      </div>
    </div>
  </div>
);

const VendorCard = ({ quote, isRecommended }) => (
  <div className={`bg-white p-6 rounded-xl shadow-lg border-2 transition-all ${
    isRecommended ? 'border-green-500 ring-2 ring-green-200' : 'border-gray-200'
  }`}>
    <div className="flex items-center justify-between mb-4">
      <h3 className="text-lg font-bold text-gray-800">{quote.vendor}</h3>
      {isRecommended && (
        <span className="bg-green-500 text-white px-2 py-1 rounded-full text-xs font-bold">
          RECOMMENDED
        </span>
      )}
    </div>
    
    <div className="space-y-3">
      <div className="flex justify-between">
        <span className="text-gray-600">Final Price:</span>
        <span className="font-bold text-lg">${quote.finalPrice.toLocaleString()}</span>
      </div>
      
      <div className="flex justify-between">
        <span className="text-gray-600">Reliability:</span>
        <span className="font-medium">{quote.avgReliability}%</span>
      </div>
      
      <div className="flex justify-between">
        <span className="text-gray-600">Lead Time:</span>
        <span className="font-medium">{quote.maxLeadTime} days</span>
      </div>
      
      <div className="flex justify-between">
        <span className="text-gray-600">Score:</span>
        <span className={`font-bold ${isRecommended ? 'text-green-600' : 'text-gray-800'}`}>
          {quote.score.toFixed(1)}/100
        </span>
      </div>
    </div>
    
    <div className="mt-4 pt-4 border-t">
      <div className="text-xs text-gray-500 space-y-1">
        <div>Subtotal: ${quote.subtotalCost.toFixed(2)}</div>
        <div>Overhead: ${quote.overheadCost.toFixed(2)}</div>
        {quote.volumeDiscount > 0 && (
          <div className="text-green-600">Volume Discount: -${quote.volumeDiscount.toFixed(2)}</div>
        )}
      </div>
    </div>
  </div>
);

const WorkflowStep = ({ status, stepKey, title, description }) => {
  const isCurrent = status === stepKey;
  const isCompleted = ['TECHNICAL_MATCHING', 'PRICING_CALCULATING', 'ORCHESTRATING', 'COMPLETE'].includes(status) &&
    (statusMap[stepKey]?.icon !== Zap || status === 'COMPLETE');
  
  let dotColor = 'bg-gray-300';
  if (isCompleted) dotColor = 'bg-green-500';
  if (isCurrent) dotColor = 'bg-indigo-500 animate-pulse';
  
  return (
    <div className="flex items-start">
      <div className={`w-3 h-3 rounded-full mt-1.5 mr-4 ${dotColor}`}></div>
      <div className="flex-1">
        <h4 className={`text-base font-semibold ${
          isCurrent ? 'text-indigo-600' : 
          isCompleted ? 'text-gray-700' : 'text-gray-500'
        }`}>
          {title}
        </h4>
        <p className="text-sm text-gray-500 mt-1">{description}</p>
      </div>
    </div>
  );
};

const ResultsPanel = ({ orchestratorOutput }) => {
  if (orchestratorOutput.error) {
    return (
      <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-xl">
        <p className="font-semibold">Error:</p>
        <p>{orchestratorOutput.error}</p>
      </div>
    );
  }

  const { pricingDetails, recommendedVendor, vendorQuotes } = orchestratorOutput;
  const vendorCount = Object.keys(vendorQuotes).length;

  return (
    <div className="bg-white p-6 rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 flex items-center">
        <CheckCircle className="w-6 h-6 text-green-500 mr-3" />
        Paint RFP Response Generated
      </h2>

      {/* Summary Stats */}
      <div className="grid grid-cols-2 md:grid-cols-5 gap-4 mb-6">
        <div className="text-center p-4 bg-indigo-50 rounded-lg">
          <div className="text-2xl font-bold text-indigo-600">{vendorCount}</div>
          <div className="text-sm text-gray-600">Paint Vendors Compared</div>
        </div>
        <div className="text-center p-4 bg-green-50 rounded-lg">
          <div className="text-2xl font-bold text-green-600">{recommendedVendor}</div>
          <div className="text-sm text-gray-600">Best Vendor</div>
        </div>
        <div className="text-center p-4 bg-purple-50 rounded-lg">
          <div className="text-2xl font-bold text-purple-600">${pricingDetails.finalPrice.toLocaleString()}</div>
          <div className="text-sm text-gray-600">Final Quote</div>
        </div>
        <div className="text-center p-4 bg-orange-50 rounded-lg">
          <div className="text-2xl font-bold text-orange-600">{pricingDetails.avgReliability}%</div>
          <div className="text-sm text-gray-600">Reliability</div>
        </div>
        <div className="text-center p-4 bg-blue-50 rounded-lg">
          <div className="text-2xl font-bold text-blue-600">{pricingDetails.maxLeadTime}</div>
          <div className="text-sm text-gray-600">Days Lead Time</div>
        </div>
      </div>

      {/* Quick Vendor Comparison */}
      <div className="border-t pt-4">
        <h3 className="text-lg font-semibold text-gray-700 mb-3">Quick Vendor Overview</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {Object.values(vendorQuotes).map((quote) => (
            <div key={quote.vendor} className={`p-4 rounded-lg border-2 ${
              quote.vendor === recommendedVendor 
                ? 'border-green-500 bg-green-50' 
                : 'border-gray-200 bg-gray-50'
            }`}>
              <div className="flex items-center justify-between mb-2">
                <span className="font-medium">{quote.vendor}</span>
                {quote.vendor === recommendedVendor && (
                  <Award className="w-4 h-4 text-green-500" />
                )}
              </div>
              <div className="text-sm text-gray-600">
                <div>${quote.finalPrice.toLocaleString()}  {quote.avgReliability}%  {quote.maxLeadTime}d</div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
