import React, { useState, useCallback, useRef } from 'react';
import { RefreshCw, Zap, Sliders, DollarSign, CheckCircle, Search, Upload, BarChart3, Users, FileText, TrendingUp, Award } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, LineChart, Line } from 'recharts';

// --- ENHANCED MOCK DATA ---
// 1. Multiple Vendor Catalogs
const VENDOR_CATALOGS = {
  'PowerCorp': [
    { id: 'PC001-A', name: 'Industrial Power Cable 5kV', insulation: 'XLPE', conductor: 'Copper', rating: 5000, cost: 12.50, reliability: 95, leadTime: 14 },
    { id: 'PC002-B', name: 'Flexible Data Cable', insulation: 'PVC', conductor: 'Aluminum', rating: 600, cost: 1.80, reliability: 88, leadTime: 7 },
    { id: 'PC003-C', name: 'Fire Resistant Cable', insulation: 'Silicon', conductor: 'Copper', rating: 1000, cost: 4.25, reliability: 92, leadTime: 10 },
  ],
  'CableTech': [
    { id: 'CT001-X', name: 'Heavy Duty Power Line', insulation: 'XLPE', conductor: 'Copper', rating: 5000, cost: 11.80, reliability: 97, leadTime: 21 },
    { id: 'CT002-Y', name: 'Standard Building Wire', insulation: 'PVC', conductor: 'Copper', rating: 600, cost: 0.95, reliability: 90, leadTime: 5 },
    { id: 'CT003-Z', name: 'High Temp Cable', insulation: 'Silicon', conductor: 'Copper', rating: 1000, cost: 4.50, reliability: 94, leadTime: 12 },
  ],
  'ElectroSupply': [
    { id: 'ES001-M', name: 'Premium Power Cable', insulation: 'XLPE', conductor: 'Copper', rating: 5000, cost: 13.20, reliability: 99, leadTime: 28 },
    { id: 'ES002-N', name: 'Economy Data Cable', insulation: 'PVC', conductor: 'Aluminum', rating: 600, cost: 1.60, reliability: 85, leadTime: 3 },
    { id: 'ES003-P', name: 'Marine Grade Cable', insulation: 'PolyEthylene', conductor: 'Fiber', rating: 20000, cost: 85.00, reliability: 96, leadTime: 35 },
  ]
};

// 2. Historical Analytics Data
const HISTORICAL_DATA = {
  rfpHistory: [
    { month: 'Jan', processed: 12, won: 8, avgValue: 45000 },
    { month: 'Feb', processed: 15, won: 11, avgValue: 52000 },
    { month: 'Mar', processed: 18, won: 13, avgValue: 48000 },
    { month: 'Apr', processed: 22, won: 16, avgValue: 55000 },
    { month: 'May', processed: 25, won: 19, avgValue: 61000 },
    { month: 'Jun', processed: 28, won: 21, avgValue: 58000 },
  ],
  vendorPerformance: [
    { vendor: 'PowerCorp', winRate: 68, avgCost: 8.50, reliability: 95 },
    { vendor: 'CableTech', winRate: 72, avgCost: 7.80, reliability: 97 },
    { vendor: 'ElectroSupply', winRate: 65, avgCost: 9.20, reliability: 99 },
