};

// 3. Enhanced Pricing Rules
const PRICING_RULES = {
  markup_percentage: 0.20,
  overhead_per_meter: 0.10,
  rush_order_multiplier: 1.15,
  volume_discounts: {
    1000: 0.02,
    5000: 0.05,
    10000: 0.08
  }
};

// --- PDF PARSING UTILITIES ---
const extractTextFromPDF = async (file) => {
  return new Promise((resolve) => {
    const reader = new FileReader();
    reader.onload = async (e) => {
      try {
        // Simulate PDF text extraction (in real implementation, use pdf-parse)
        const mockExtractedText = `
REQUEST FOR PROPOSAL - Tender #${Math.random().toString(36).substr(2, 9).toUpperCase()}
Deadline: ${new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]}

PROJECT REQUIREMENTS:
We require ${Math.floor(Math.random() * 8000) + 2000} meters of cable for industrial facility upgrade.

Requirement A: Heavy-Duty Cable (${Math.floor(Math.random() * 4000) + 2000}m)
- Insulation Material: XLPE or similar high-performance polymer
- Conductor: Must be Copper
- Voltage Rating: Must handle minimum of ${Math.floor(Math.random() * 2000) + 3000}V

Requirement B: Standard Cable (${Math.floor(Math.random() * 3000) + 1000}m)  
- Insulation Material: PVC
- Conductor: Must be Copper
- Voltage Rating: Must handle up to 600V
- Note: Include certification test fee of $${Math.floor(Math.random() * 1000) + 1000}
        `;
        
        setTimeout(() => resolve(mockExtractedText), 1500); // Simulate processing time
      } catch (error) {
        resolve("Error parsing PDF. Using default RFP text.");
      }
    };
    reader.readAsArrayBuffer(file);
  });
};

const parseRFPRequirements = (text) => {
  // Enhanced parsing logic
  const lines = text.split('\n');
  const requirements = [];
  let currentReq = null;
  
  lines.forEach(line => {
    if (line.includes('Requirement A') || line.includes('Heavy-Duty')) {
      const lengthMatch = line.match(/(\d+)m/);
      const ratingMatch = text.match(/minimum of (\d+)V/);
      requirements.push({
        id: 'A',
        length: lengthMatch ? parseInt(lengthMatch[1]) : 3000,
        specs: {
          insulation: 'XLPE',
          conductor: 'Copper',
          minRating: ratingMatch ? parseInt(ratingMatch[1]) : 4000
        }
      });
    }
    if (line.includes('Requirement B') || line.includes('Standard Cable')) {
      const lengthMatch = line.match(/(\d+)m/);
      const feeMatch = text.match(/\$(\d+)/);
      requirements.push({
        id: 'B',
        length: lengthMatch ? parseInt(lengthMatch[1]) : 2000,
        specs: {
          insulation: 'PVC',
          conductor: 'Copper',
          maxRating: 600,
          testFee: feeMatch ? parseInt(feeMatch[1]) : 1500
        }
      });
    }
  });
  
  const deadlineMatch = text.match(/Deadline: (\d{4}-\d{2}-\d{2})/);
  const totalMatch = text.match(/(\d+) meters/);
  
  return {
    deadline: deadlineMatch ? deadlineMatch[1] : '2024-12-15',
    totalMeters: totalMatch ? parseInt(totalMatch[1]) : 5000,
    requirements: requirements.length > 0 ? requirements : [
      { id: 'A', length: 3000, specs: { insulation: 'XLPE', conductor: 'Copper', minRating: 4000 } },
      { id: 'B', length: 2000, specs: { insulation: 'PVC', conductor: 'Copper', maxRating: 600, testFee: 1500 } }
    ]
  };
};

// --- ENHANCED AGENT LOGIC ---
const delay = (ms) => new Promise(resolve => setTimeout(resolve, ms));

// Enhanced Sales Agent with PDF support
const salesAgent = async (rfpText, isFromPDF = false) => {
  await delay(isFromPDF ? 2000 : 1000);
  console.log(`Sales Agent: ${isFromPDF ? 'Processing uploaded PDF' : 'Processing text input'}...`);
  
  return parseRFPRequirements(rfpText);
};

// Multi-Vendor Technical Agent
const technicalAgent = async (salesOutput) => {
  await delay(2500);
  console.log("Technical Agent: Comparing across multiple vendors...");
  
  const matchedRequirements = salesOutput.requirements.map(req => {
    const vendorMatches = {};
    
    // Check each vendor
    Object.entries(VENDOR_CATALOGS).forEach(([vendorName, catalog]) => {
      let bestMatch = null;
      let maxScore = -1;
      
      catalog.forEach(sku => {
        let score = 0;
        let reason = [];
        
        // Insulation match
        if (sku.insulation.toLowerCase() === req.specs.insulation.toLowerCase()) {
          score += 40;
          reason.push("Insulation Match (40%)");
        }
        
        // Conductor match
        if (sku.conductor.toLowerCase() === req.specs.conductor.toLowerCase()) {
          score += 40;
          reason.push("Conductor Match (40%)");
        }
        
        // Rating check
        if (req.specs.minRating && sku.rating >= req.specs.minRating) {
          score += 20;
          reason.push("Voltage Rating Met (20%)");
        } else if (req.specs.maxRating && sku.rating <= req.specs.maxRating) {
          score += 20;
          reason.push("Voltage Rating Met (20%)");
        }
        
        if (score > maxScore) {
          maxScore = score;
          bestMatch = {
            skuId: sku.id,
            skuName: sku.name,
            skuCost: sku.cost,
            reliability: sku.reliability,
            leadTime: sku.leadTime,
            matchScore: maxScore,
            matchReason: reason.join(', '),
          };
        }
      });
      
      if (bestMatch) {
        vendorMatches[vendorName] = bestMatch;
      }
    });
    
    return { ...req, vendorMatches };
  });
  
  return { deadline: salesOutput.deadline, matchedRequirements };
};

// Enhanced Pricing Agent with multi-vendor comparison
const pricingAgent = async (technicalOutput) => {
  await delay(2000);
  console.log("Pricing Agent: Calculating costs across all vendors...");
  
  const vendorQuotes = {};
  
  // Calculate quote for each vendor
  Object.keys(VENDOR_CATALOGS).forEach(vendorName => {
    let subtotalCost = 0;
    let totalTestFees = 0;
    let totalLength = 0;
    let avgReliability = 0;
    let maxLeadTime = 0;
    let validQuote = true;
    
    const vendorItems = [];
    
    technicalOutput.matchedRequirements.forEach(item => {
      const vendorMatch = item.vendorMatches[vendorName];
      if (vendorMatch) {
        const costPerMeter = vendorMatch.skuCost;
        const length = item.length;
        const itemCost = costPerMeter * length;
        
        subtotalCost += itemCost;
        totalLength += length;
        avgReliability += vendorMatch.reliability;
        maxLeadTime = Math.max(maxLeadTime, vendorMatch.leadTime);
        
        if (item.specs.testFee) {
          totalTestFees += item.specs.testFee;
        }
        
        vendorItems.push({
          requirement: item.id,
          sku: vendorMatch.skuId,
          cost: itemCost,
          reliability: vendorMatch.reliability,
          leadTime: vendorMatch.leadTime
        });
      } else {
        validQuote = false;
      }
    });
    
    if (validQuote && vendorItems.length > 0) {
      avgReliability = avgReliability / vendorItems.length;
      
      // Apply volume discounts
      let volumeDiscount = 0;
      Object.entries(PRICING_RULES.volume_discounts).forEach(([threshold, discount]) => {
        if (totalLength >= parseInt(threshold)) {
          volumeDiscount = Math.max(volumeDiscount, discount);
        }
      });
      
      const overheadCost = totalLength * PRICING_RULES.overhead_per_meter;
      const discountAmount = subtotalCost * volumeDiscount;
      const grossCost = subtotalCost + overheadCost + totalTestFees - discountAmount;
      const finalPrice = grossCost * (1 + PRICING_RULES.markup_percentage);
      
      vendorQuotes[vendorName] = {
        vendor: vendorName,
        subtotalCost,
        overheadCost,
        totalTestFees,
        volumeDiscount: discountAmount,
        finalPrice: parseFloat(finalPrice.toFixed(2)),
        avgReliability: parseFloat(avgReliability.toFixed(1)),
        maxLeadTime,
        totalLength,
        items: vendorItems,
        score: calculateVendorScore(finalPrice, avgReliability, maxLeadTime)
      };
    }
  });
  
  // Select best vendor
  const bestVendor = Object.values(vendorQuotes).reduce((best, current) => 
    current.score > best.score ? current : best
  );
  
  return {
    ...technicalOutput,
    vendorQuotes,
    recommendedVendor: bestVendor.vendor,
    pricingDetails: bestVendor
  };
};

// Vendor scoring algorithm
const calculateVendorScore = (price, reliability, leadTime) => {
  const priceScore = Math.max(0, 100 - (price / 1000)); // Lower price = higher score
  const reliabilityScore = reliability; // Direct reliability score
  const timeScore = Math.max(0, 100 - leadTime * 2); // Faster delivery = higher score
  
  return (priceScore * 0.4 + reliabilityScore * 0.4 + timeScore * 0.2);
};

