// Enhanced Helper Functions for Real-World Calculations

// Get current season for pricing adjustments
const getCurrentSeason = () => {
  const month = new Date().getMonth();
  if (month >= 11 || month <= 2) return 'winter';
  if (month >= 3 && month <= 5) return 'summer';
  return 'monsoon';
};

// Assess project complexity based on requirements
const assessProjectComplexity = requirements => {
  const totalArea = requirements.reduce((sum, req) => sum + req.area, 0);
  const uniqueFinishes = new Set(requirements.map(req => req.specs.finish)).size;
  const hasExterior = requirements.some(req => req.type === 'exterior');
  const hasInterior = requirements.some(req => req.type === 'interior');
  if (uniqueFinishes > 3 || hasExterior && hasInterior && totalArea > 50000) {
    return 'complex';
  } else if (uniqueFinishes > 1 || hasExterior && hasInterior) {
    return 'moderate';
  }
  return 'simple';
};

// Assess urgency level based on deadline
const assessUrgencyLevel = deadline => {
  const deadlineDate = new Date(deadline);
  const today = new Date();
  const daysUntilDeadline = Math.ceil((deadlineDate - today) / (1000 * 60 * 60 * 24));
  if (daysUntilDeadline < 7) return 'emergency';
  if (daysUntilDeadline < 14) return 'urgent';
  if (daysUntilDeadline < 30) return 'expedited';
  return 'standard';
};

// Get waste factor based on surface type and complexity
const getWasteFactor = (surfaceType, complexity) => {
  const baseWaste = PRICING_RULES.waste_factors[surfaceType] || PRICING_RULES.waste_factors.interior;
  const complexityMultiplier = complexity === 'complex' ? 1.2 : complexity === 'moderate' ? 1.1 : 1.0;
  return baseWaste * complexityMultiplier;
};

// Calculate bulk discount based on quantity
const getBulkDiscount = (quantity, bulkDiscounts) => {
  let maxDiscount = 0;
  Object.entries(bulkDiscounts).forEach(([threshold, discount]) => {
    if (quantity >= parseInt(threshold)) {
      maxDiscount = Math.max(maxDiscount, discount);
    }
  });
  return maxDiscount;
};

// Calculate labor cost with complexity factors
const calculateLaborCost = (area, specs, complexity) => {
  let baseLaborCost = area * PRICING_RULES.labor_factors.base_rate_per_sqft;

  // Apply complexity multipliers
  if (specs.highCeilings) baseLaborCost *= PRICING_RULES.labor_factors.complexity_multipliers.high_ceilings;
  if (specs.difficultAccess) baseLaborCost *= PRICING_RULES.labor_factors.complexity_multipliers.difficult_access;
  if (specs.multipleCoats) baseLaborCost *= PRICING_RULES.labor_factors.complexity_multipliers.multiple_coats;
  if (specs.specialPrep) baseLaborCost *= PRICING_RULES.labor_factors.complexity_multipliers.special_prep;
  return baseLaborCost;
};

// Get project size category
const getProjectSize = totalArea => {
  if (totalArea < 10000) return 'small';
  if (totalArea < 50000) return 'medium';
  return 'large';
};

// Enhanced vendor scoring algorithm
const calculateEnhancedVendorScore = (price, reliability, leadTime, certificationCount) => {
  const priceScore = Math.max(0, 100 - price / 1000); // Lower price = higher score
  const reliabilityScore = reliability; // Direct reliability score
  const timeScore = Math.max(0, 100 - leadTime * 2); // Faster delivery = higher score
  const certificationScore = Math.min(100, certificationCount * 20); // More certifications = higher score

  return priceScore * 0.3 + reliabilityScore * 0.3 + timeScore * 0.2 + certificationScore * 0.2;
};

// Legacy vendor scoring algorithm (kept for backward compatibility)
const calculateVendorScore = (price, reliability, leadTime) => {
  const priceScore = Math.max(0, 100 - price / 1000); // Lower price = higher score
  const reliabilityScore = reliability; // Direct reliability score
  const timeScore = Math.max(0, 100 - leadTime * 2); // Faster delivery = higher score

  return priceScore * 0.4 + reliabilityScore * 0.4 + timeScore * 0.2;
};

