      name: 'Bison Acrylic Distemper', 
      finish: 'Matt', 
      coverage: 100, 
      durability: 6, 
      cost: 165.40, 
      reliability: 85, 
      leadTime: 2,
      availability: 'high',
      regionalSupport: ['all'],
      certifications: ['ISO9001'],
      bulkDiscounts: { 2000: 0.06, 10000: 0.13, 25000: 0.20 },
      seasonalPricing: { winter: 0.90, summer: 1.10, monsoon: 1.18 }
    },
  ],
  'Nerolac Paints': [
    { 
      id: 'NP001-M', 
      name: 'Excel Total Exterior', 
      finish: 'Sheen', 
      coverage: 130, 
