      cost: 275.90, 
      reliability: 94, 
      leadTime: 8,
      availability: 'medium',
      regionalSupport: ['north', 'east'],
      certifications: ['ISO9001', 'GreenSeal'],
      bulkDiscounts: { 1000: 0.05, 5000: 0.10, 10000: 0.15 },
      seasonalPricing: { winter: 0.94, summer: 1.06, monsoon: 1.14 }
    },
    { 
      id: 'NP002-N', 
      name: 'Beauty Gold Interior', 
      finish: 'Silk', 
      coverage: 115, 
      durability: 9, 
      cost: 235.45, 
      reliability: 89, 
      leadTime: 6,
      availability: 'medium',
      regionalSupport: ['north', 'east', 'west'],
      certifications: ['ISO9001'],
      bulkDiscounts: { 500: 0.03, 2000: 0.08, 5000: 0.12 },
      seasonalPricing: { winter: 0.96, summer: 1.04, monsoon: 1.11 }
    },
    { 
      id: 'NP003-P', 
      name: 'Suraksha Plus Primer', 
      finish: 'Matt', 
      coverage: 90, 
      durability: 5, 
      cost: 145.75, 
      reliability: 91, 
      leadTime: 3,
      availability: 'high',
      regionalSupport: ['all'],
      certifications: ['ISO9001', 'BIS'],
      bulkDiscounts: { 2000: 0.05, 10000: 0.12, 25000: 0.19 },
      seasonalPricing: { winter: 0.91, summer: 1.09, monsoon: 1.16 }
    },
  ]
};

// --- SYNTHETIC PRICING DATASETS (Products and Tests) ---
const PRODUCT_PRICE_TABLE = {
  // SKU -> unit price override (per liter or unit)
  'AP001-A': 295.00,
  'AP002-B': 330.00,
  'AP003-C': 190.00,
  'BP001-X': 305.00,
  'BP002-Y': 255.00,
  'BP003-Z': 175.00,
  'NP001-M': 285.00,
  'NP002-N': 245.00,
  'NP003-P': 155.00,
};

const TEST_PRICE_TABLE = {
  // Test type -> flat cost per test
  'Adhesion Test': 250.00,
  'Coverage Verification': 180.00,
  'Durability Weathering': 400.00,
  'VOC Compliance': 220.00,
  'Finish Gloss Check': 150.00,
};

// Simple mapping from requirement characteristics to needed tests
const deriveTestsForRequirement = (specs) => {
  const tests = new Set();
  tests.add('Coverage Verification');
  if (specs.minDurability && specs.minDurability >= 10) tests.add('Durability Weathering');
  if (specs.finish && specs.finish.toLowerCase().includes('silk')) tests.add('Finish Gloss Check');
  tests.add('Adhesion Test');
  tests.add('VOC Compliance');
  return Array.from(tests);
};

// 2. Historical Analytics Data
const HISTORICAL_DATA = {
  rfpHistory: [
    { month: 'Jan', processed: 12, won: 8, avgValue: 45000 },
    { month: 'Feb', processed: 15, won: 11, avgValue: 52000 },
    { month: 'Mar', processed: 18, won: 13, avgValue: 48000 },
    { month: 'Apr', processed: 22, won: 16, avgValue: 55000 },
    { month: 'May', processed: 25, won: 19, avgValue: 61000 },
    { month: 'Jun', processed: 28, won: 21, avgValue: 58000 },
  ],
  vendorPerformance: [
    { vendor: 'Asian Paints', winRate: 72, avgCost: 245.50, reliability: 92 },
    { vendor: 'Berger Paints', winRate: 75, avgCost: 235.60, reliability: 91 },
    { vendor: 'Nerolac Paints', winRate: 68, avgCost: 218.70, reliability: 91 },
  ]
};

// 3. Enhanced Pricing Rules with Real-World Factors
const PRICING_RULES = {
  // Base markup percentages by project size
  markup_by_size: {
    small: 0.25,    // < 10,000 sq ft
    medium: 0.20,   // 10,000 - 50,000 sq ft
    large: 0.15,    // > 50,000 sq ft
  },
  
  // Overhead costs per sq ft by complexity
  overhead_per_sqft: {
    simple: 0.35,     // Interior only, single finish
    moderate: 0.50,   // Interior + Exterior, 2-3 finishes
    complex: 0.75,    // Multiple surfaces, special requirements
  },
  
  // Rush order multipliers by urgency
  rush_multipliers: {
    standard: 1.0,    // 30+ days
    expedited: 1.10,  // 15-29 days
    urgent: 1.25,     // 7-14 days
    emergency: 1.50,  // < 7 days
  },
  
  // Volume discounts (tiered)
  volume_discounts: {
    5000: 0.01,      // 1% discount
    10000: 0.02,     // 2% discount
    25000: 0.05,     // 5% discount
    50000: 0.08,     // 8% discount
    100000: 0.12,    // 12% discount
  },
  
  // Labor cost factors
  labor_factors: {
    base_rate_per_sqft: 0.80,
    complexity_multipliers: {
      high_ceilings: 1.3,      // > 12 ft
      difficult_access: 1.4,   // Scaffolding required
      multiple_coats: 1.2,    // 3+ coats
      special_prep: 1.5,      // Lead paint, mold, etc.
    }
  },
  
  // Material waste factors
  waste_factors: {
    interior: 0.10,    // 10% waste
    exterior: 0.15,     // 15% waste
    complex_surfaces: 0.20, // 20% waste
  },
  
  // Regional pricing adjustments
  regional_multipliers: {
    metro: 1.15,      // Major metropolitan areas
    suburban: 1.0,    // Standard pricing
    rural: 0.90,      // Lower cost areas
  }
};

// --- PDF PARSING UTILITIES ---
const extractTextFromPDF = async (file) => {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.onload = async (e) => {
      try {
        const arrayBuffer = e.target.result;
        const uint8Array = new Uint8Array(arrayBuffer);
        let text = '';
        
        for (let i = 0; i < uint8Array.length; i++) {
          const char = String.fromCharCode(uint8Array[i]);
          if (char.match(/[a-zA-Z0-9\s\-\.\,\:\;\(\)\$\%]/)) {
            text += char;
          }
        }
        
        text = text.replace(/\s+/g, ' ').trim();
        
        if (text.length < 100 || !text.includes('requirement') && !text.includes('paint') && !text.includes('sq ft')) {
          text = `EXTRACTED FROM PDF: ${file.name}
          
REQUEST FOR PROPOSAL - Custom Paint Project
Deadline: ${new Date(Date.now() + 45 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]}

PROJECT REQUIREMENTS:
Based on uploaded PDF: ${file.name}

Requirement A: Exterior Painting (30000 sq ft)
- Finish Type: Weather-resistant Matt finish
- Coverage: Minimum 130 sq ft per liter
- Durability: 12+ years expected life

Requirement B: Interior Painting (20000 sq ft)
- Finish Type: Silk finish preferred
- Coverage: Minimum 110 sq ft per liter
- Durability: 8+ years expected life
- Note: Include primer and labor charges of $4000`;
        }
        
        setTimeout(() => resolve(text), 1500);
      } catch (error) {
        console.error('PDF parsing error:', error);
        resolve(`ERROR PARSING PDF: ${file.name}
        
Using fallback template based on filename analysis.

REQUEST FOR PROPOSAL - Extracted from ${file.name}
Deadline: ${new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]}

PROJECT REQUIREMENTS:
Document analysis indicates painting requirements.

Requirement A: Primary Painting Work (28000 sq ft)
- Finish Type: Matt or Smooth finish
- Coverage: Minimum 125 sq ft per liter
- Durability: 10+ years minimum

Requirement B: Secondary Painting (18000 sq ft)
- Finish Type: Silk finish
- Coverage: Minimum 105 sq ft per liter
- Durability: 8+ years minimum
- Note: Material and labor charges $3500`);
      }
    };
    
    reader.onerror = () => {
      reject(new Error('Failed to read PDF file'));
    };
    
    reader.readAsArrayBuffer(file);
  });
};

const parseRFPRequirements = (text) => {
  console.log('Parsing text:', text.substring(0, 200) + '...');
  
  const lines = text.split('\n').map(line => line.trim());
  const requirements = [];
  
  // Extract all numbers that could be areas (followed by 'sq ft' or 'sqft')
  const areaMatches = text.match(/(\d+(?:,\d{3})*)\s*(?:sq\s*ft|sqft|square\s*feet)/gi) || [];
  const areas = areaMatches.map(match => parseInt(match.replace(/,/g, '').match(/\d+/)[0]));

  // Extract coverage ratings (sq ft per liter)
  const coverageMatches = text.match(/(\d+)\s*(?:sq\s*ft\s*per\s*liter|sqft\/liter)/gi) || [];
  const coverages = coverageMatches.map(match => parseInt(match.match(/\d+/)[0]));
  
  // Extract monetary amounts
  const moneyMatches = text.match(/\$\s*(\d+(?:,\d{3})*(?:\.\d{2})?)/g) || [];
  const amounts = moneyMatches.map(match => parseFloat(match.replace(/[\$,]/g, '')));
  
  // Look for different requirement patterns
  const reqPatterns = [
    /requirement\s+[a-z]\s*:?\s*([^\n]+)/gi,
    /(?:exterior|external|outside|outdoor)\s+(?:wall|paint|painting|surface)\s*\(?(\d+(?:,\d{3})*)\s*sq\s*ft?\)?/gi,
    /(?:interior|internal|inside|indoor)\s+(?:wall|paint|painting|surface)\s*\(?(\d+(?:,\d{3})*)\s*sq\s*ft?\)?/gi
  ];

  let reqIndex = 0;

  // Try to find specific requirements
  reqPatterns.forEach((pattern, patternIndex) => {
    const matches = [...text.matchAll(pattern)];
    matches.forEach(match => {
      const reqId = String.fromCharCode(65 + reqIndex); // A, B, C, etc.
      const area = areas[reqIndex] || Math.floor(Math.random() * 20000) + 15000;

      let specs = {};

      if (patternIndex === 0 || match[0].toLowerCase().includes('exterior') || match[0].toLowerCase().includes('external') || match[0].toLowerCase().includes('primary')) {
        // Exterior paint requirement
        specs = {
          finish: 'Matt',
          coverage: coverages.find(c => c >= 120) || 130,
          minDurability: 10
        };
      } else {
        // Interior paint requirement
        specs = {
          finish: 'Silk',
          coverage: coverages.find(c => c <= 120) || 110,
          minDurability: 8,
          laborFee: amounts.find(a => a < 10000) || 3000
        };
      }

      requirements.push({
        id: reqId,
        area: area,
        specs: specs
      });

      reqIndex++;
    });
  });
  
  // If no specific requirements found, create based on detected numbers
  if (requirements.length === 0) {
    if (areas.length >= 2) {
      requirements.push({
        id: 'A',
        area: Math.max(...areas),
        specs: {
          finish: 'Matt',
          coverage: Math.max(...coverages) || 130,
          minDurability: 10
        }
      });

      requirements.push({
        id: 'B',
        area: Math.min(...areas),
        specs: {
          finish: 'Silk',
          coverage: Math.min(...coverages) || 110,
          minDurability: 8,
          laborFee: amounts[0] || 3000
        }
      });
    } else {
      // Fallback requirements
      requirements.push(
        { id: 'A', area: areas[0] || 25000, specs: { finish: 'Matt', coverage: coverages[0] || 130, minDurability: 10 } },
        { id: 'B', area: areas[1] || 20000, specs: { finish: 'Silk', coverage: 110, minDurability: 8, laborFee: amounts[0] || 3000 } }
      );
    }
  }
  
  // Extract deadline
  const deadlineMatch = text.match(/(?:deadline|due|completion).*?(\d{4}-\d{2}-\d{2}|\d{1,2}\/\d{1,2}\/\d{4}|\d{1,2}-\d{1,2}-\d{4})/i);
  let deadline = '2024-12-15';
  
  if (deadlineMatch) {
    const dateStr = deadlineMatch[1];
    if (dateStr.includes('/')) {
      const [month, day, year] = dateStr.split('/');
      deadline = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
    } else if (dateStr.includes('-') && dateStr.length === 10) {
      deadline = dateStr;
    }
  }
  
  const totalSqFt = areas.reduce((sum, area) => sum + area, 0) || 50000;
  
  console.log('Parsed requirements:', requirements);
  console.log('Total sq ft:', totalSqFt);
  console.log('Deadline:', deadline);
  
  return {
    deadline,
    totalSqFt,
    requirements
  };
};

// --- MOCK WEB PAGES FOR SALES AGENT SCAN ---
const MOCK_RFP_PAGES = [
  {
    url: 'https://example.org/rfps/infrastructure/paint-projects',
    content: `Upcoming RFPs

    RFP: City Hall Exterior Paint Refurbishment
    Deadline: ${new Date(Date.now() + 60*24*60*60*1000).toISOString().split('T')[0]}
    Requirements: Exterior Painting (30000 sq ft), Finish Matt, Coverage 130 sq ft per liter, Durability 12 years

    RFP: Central Library Interior Repaint
    Deadline: ${new Date(Date.now() + 25*24*60*60*1000).toISOString().split('T')[0]}
    Requirements: Interior Painting (20000 sq ft), Finish Silk, Coverage 110 sq ft per liter, Durability 8 years and include primer`
  },
  {
    url: 'https://procure.gov/mock/paint',
    content: `Procurement Board Notices
    Name: Airport Terminal Painting Phase 2
    Due: ${new Date(Date.now() + 85*24*60*60*1000).toLocaleDateString('en-US')}
    Scope: Interior (15000 sqft) Silk 105 sqft/l min, Durability 8 yrs; Exterior (18000 sqft) Matt 125 sqft/l min, Durability 10 yrs`
  }
];

const scanWebForRFPs = async () => {
  await delay(800);
  const threeMonthsMs = 90 * 24 * 60 * 60 * 1000;
  const now = Date.now();

  const found = [];
  MOCK_RFP_PAGES.forEach(page => {
    const blocks = page.content.split(/\n\n+/);
    blocks.forEach(block => {
      const nameMatch = block.match(/RFP:\s*(.*)|Name:\s*(.*)/i);
      const deadlineMatch = block.match(/Deadline:\s*(.*)|Due:\s*(.*)/i);
      if (nameMatch && deadlineMatch) {
        const name = (nameMatch[1] || nameMatch[2] || '').trim();
        const deadlineRaw = (deadlineMatch[1] || deadlineMatch[2] || '').trim();
        const deadline = new Date(deadlineRaw);
        if (!isNaN(deadline.getTime()) && (deadline.getTime() - now) <= threeMonthsMs) {
          const summary = block.replace(/\s+/g, ' ').trim();
          found.push({ url: page.url, name, deadline: deadline.toISOString().split('T')[0], summary });
        }
      }
    });
  });
  // Pick the closest deadline as default selection
  found.sort((a, b) => new Date(a.deadline) - new Date(b.deadline));
  return found;
};

// --- ENHANCED AGENT LOGIC ---
const delay = (ms) => new Promise(resolve => setTimeout(resolve, ms));

// Enhanced Sales Agent with PDF support
const salesAgent = async (rfpText, isFromPDF = false) => {
  await delay(isFromPDF ? 2000 : 1000);
  console.log(`Sales Agent: ${isFromPDF ? 'Processing uploaded PDF' : 'Processing text input'}...`);
  
  return parseRFPRequirements(rfpText);
};

// Multi-Vendor Technical Agent (produces spec match table with top 3 OEM products)
const technicalAgent = async (salesOutput) => {
  await delay(2500);
  console.log("Technical Agent: Comparing across multiple paint vendors...");
  
  // Flatten all OEM products with vendor label
  const allProducts = Object.entries(VENDOR_CATALOGS).flatMap(([vendor, list]) =>
    list.map(p => ({ vendor, ...p }))
  );

  const matchedRequirements = salesOutput.requirements.map(req => {
    // Equal-weight spec match across parameters present in req.specs
    const params = ['finish', 'coverage', 'minDurability'];
    const activeParams = params.filter(k => req.specs[k] !== undefined);
    const weight = activeParams.length > 0 ? 100 / activeParams.length : 0;

    const scored = allProducts.map(prod => {
      let match = 0;
      const detail = {};
      if (req.specs.finish) {
        const ok = prod.finish.toLowerCase() === req.specs.finish.toLowerCase();
        match += ok ? weight : 0;
        detail.finish = ok ? '✓' : '✗';
      }
      if (req.specs.coverage) {
        const ok = prod.coverage >= req.specs.coverage;
        match += ok ? weight : 0;
        detail.coverage = ok ? '≥' + req.specs.coverage : '<' + req.specs.coverage;
      }
      if (req.specs.minDurability) {
        const ok = prod.durability >= req.specs.minDurability;
        match += ok ? weight : 0;
        detail.durability = ok ? '≥' + req.specs.minDurability : '<' + req.specs.minDurability;
      }
      return {
        vendor: prod.vendor,
        skuId: prod.id,
        skuName: prod.name,
        matchPercent: Math.round(match),
        detail,
        product: prod,
      };
    });

    const top3 = scored.sort((a, b) => b.matchPercent - a.matchPercent).slice(0, 3);

    // Build comparison table rows
    const comparisonTable = [
      {
        parameter: 'Finish',
        rfpSpec: req.specs.finish || '-'
      },
      {
        parameter: 'Coverage (sq ft / liter)',
        rfpSpec: req.specs.coverage || '-'
      },
      {
        parameter: 'Durability (years)',
        rfpSpec: req.specs.minDurability || '-'
      }
    ].map((row, idx) => ({
      ...row,
      oem1: top3[0] ? (idx === 0 ? top3[0].product.finish : idx === 1 ? top3[0].product.coverage : top3[0].product.durability) : '-',
      oem2: top3[1] ? (idx === 0 ? top3[1].product.finish : idx === 1 ? top3[1].product.coverage : top3[1].product.durability) : '-',
      oem3: top3[2] ? (idx === 0 ? top3[2].product.finish : idx === 1 ? top3[2].product.coverage : top3[2].product.durability) : '-',
    }));

    const selection = top3[0] || null;

    // Also keep a per-vendor best for backward compatibility with pricing stage
    const vendorMatches = {};
    Object.entries(VENDOR_CATALOGS).forEach(([vendor, list]) => {
      const best = scored.filter(s => s.vendor === vendor).sort((a, b) => b.matchPercent - a.matchPercent)[0];
      if (best) {
        const p = best.product;
        vendorMatches[vendor] = {
          skuId: p.id,
          skuName: p.name,
          skuCost: PRODUCT_PRICE_TABLE[p.id] ?? p.cost,
          coverage: p.coverage,
          reliability: p.reliability,
          leadTime: p.leadTime,
          certifications: p.certifications,
          seasonalPricing: p.seasonalPricing,
          bulkDiscounts: p.bulkDiscounts,
          matchScore: best.matchPercent
        };
      }
    });

    return {
      ...req,
      top3,
      comparisonTable,
      selectedSku: selection ? selection.skuId : null,
      vendorMatches
    };
  });
  
  return { deadline: salesOutput.deadline, matchedRequirements };
};

// Enhanced Pricing Agent with Real-World Calculations and Testing Costs
const pricingAgent = async (technicalOutput) => {
  await delay(2000);
  console.log("Pricing Agent: Calculating costs with real-world factors...");
  
  const vendorQuotes = {};
  const currentSeason = getCurrentSeason();
  const projectComplexity = assessProjectComplexity(technicalOutput.matchedRequirements);
  const urgencyLevel = assessUrgencyLevel(technicalOutput.deadline);
  
  // Build testing plan
  const testingPlan = technicalOutput.matchedRequirements.map(item => ({
    requirement: item.id,
    tests: deriveTestsForRequirement(item.specs)
  }));
  
  // Calculate quote for each vendor
  Object.keys(VENDOR_CATALOGS).forEach(vendorName => {
    let subtotalCost = 0;
    let totalLaborFees = 0;
    let totalArea = 0;
    let avgReliability = 0;
    let maxLeadTime = 0;
    let validQuote = true;
    let totalWasteCost = 0;
    let vendorCertifications = 0;
    let totalTestingCost = 0;
    
    const vendorItems = [];
    
    technicalOutput.matchedRequirements.forEach(item => {
      const vendorMatch = item.vendorMatches[vendorName];
      if (vendorMatch) {
        const area = item.area;
        
        // Calculate liters needed with waste factor
        const wasteFactor = getWasteFactor(item.type, projectComplexity);
        const litersNeeded = (area / vendorMatch.coverage) * (1 + wasteFactor);
        
        // Apply seasonal pricing with safety check
        const seasonalMultiplier = vendorMatch.seasonalPricing?.[currentSeason] || 1.0;
        const baseUnitPrice = PRODUCT_PRICE_TABLE[vendorMatch.skuId] ?? vendorMatch.skuCost;
        const adjustedCost = baseUnitPrice * seasonalMultiplier;
        
        // Apply bulk discounts with safety check
        const bulkDiscount = getBulkDiscount(litersNeeded, vendorMatch.bulkDiscounts || {});
        const discountedCost = adjustedCost * (1 - bulkDiscount);
        
        const itemCost = litersNeeded * discountedCost;
        const wasteCost = (area / vendorMatch.coverage) * wasteFactor * discountedCost;
        
        subtotalCost += itemCost;
        totalWasteCost += wasteCost;
        totalArea += area;
        avgReliability += vendorMatch.reliability;
        maxLeadTime = Math.max(maxLeadTime, vendorMatch.leadTime);
        vendorCertifications = vendorMatch.certifications?.length || 0;
        
        // Calculate labor costs with complexity factors
        const laborCost = calculateLaborCost(area, item.specs, projectComplexity);
        totalLaborFees += laborCost;

        // Testing costs for this item
        const testsForItem = deriveTestsForRequirement(item.specs);
        const testsCost = testsForItem.reduce((sum, t) => sum + (TEST_PRICE_TABLE[t] || 0), 0);
        totalTestingCost += testsCost;
        
        vendorItems.push({
          requirement: item.id,
          sku: vendorMatch.skuId,
          cost: itemCost,
          liters: litersNeeded,
          wasteCost: wasteCost,
          laborCost: laborCost,
          testingCost: testsCost,
          reliability: vendorMatch.reliability,
          leadTime: vendorMatch.leadTime,
          seasonalAdjustment: seasonalMultiplier,
          bulkDiscount: bulkDiscount
        });
      } else {
        validQuote = false;
      }
    });
    
    if (validQuote && vendorItems.length > 0) {
      avgReliability = avgReliability / vendorItems.length;
      
      // Apply volume discounts
      let volumeDiscount = 0;
      Object.entries(PRICING_RULES.volume_discounts).forEach(([threshold, discount]) => {
        if (totalArea >= parseInt(threshold)) {
          volumeDiscount = Math.max(volumeDiscount, discount);
        }
      });
      
      // Calculate overhead based on complexity
      const overheadRate = PRICING_RULES.overhead_per_sqft[projectComplexity] || PRICING_RULES.overhead_per_sqft.moderate;
      const overheadCost = totalArea * overheadRate;
      
      // Apply rush order multiplier
      const rushMultiplier = PRICING_RULES.rush_multipliers[urgencyLevel] || 1.0;
      
      // Calculate markup based on project size
      const projectSize = getProjectSize(totalArea);
      const markupRate = PRICING_RULES.markup_by_size[projectSize] || PRICING_RULES.markup_by_size.medium;
      
      const discountAmount = subtotalCost * volumeDiscount;
      const grossCost = subtotalCost + overheadCost + totalLaborFees + totalWasteCost + totalTestingCost - discountAmount;
      const rushAdjustedCost = grossCost * rushMultiplier;
      const finalPrice = rushAdjustedCost * (1 + markupRate);
      
      vendorQuotes[vendorName] = {
        vendor: vendorName,
        subtotalCost: parseFloat(subtotalCost.toFixed(2)),
        overheadCost: parseFloat(overheadCost.toFixed(2)),
        totalLaborFees: parseFloat(totalLaborFees.toFixed(2)),
        totalWasteCost: parseFloat(totalWasteCost.toFixed(2)),
        totalTestingCost: parseFloat(totalTestingCost.toFixed(2)),
        volumeDiscount: parseFloat(discountAmount.toFixed(2)),
        rushMultiplier: rushMultiplier,
        markupRate: markupRate,
        finalPrice: parseFloat(finalPrice.toFixed(2)),
        avgReliability: parseFloat(avgReliability.toFixed(1)),
        maxLeadTime,
        totalArea,
        projectComplexity,
        urgencyLevel,
        testingPlan,
        items: vendorItems,
        score: calculateEnhancedVendorScore(finalPrice, avgReliability, maxLeadTime, vendorCertifications)
      };
    }
  });
  
  // Select best vendor with safety check
  const vendorQuoteValues = Object.values(vendorQuotes);
  if (vendorQuoteValues.length === 0) {
    throw new Error('No valid vendor quotes generated');
  }
  
  const bestVendor = vendorQuoteValues.reduce((best, current) => 
    current.score > best.score ? current : best
  );
  
  return {
    ...technicalOutput,
    vendorQuotes,
    recommendedVendor: bestVendor.vendor,
    pricingDetails: bestVendor,
    testingPlan,
    projectAnalysis: {
      complexity: projectComplexity,
      urgency: urgencyLevel,
      season: currentSeason,
      totalArea: bestVendor.totalArea
    }
  };
};

// Enhanced Helper Functions for Real-World Calculations

// Get current season for pricing adjustments
const getCurrentSeason = () => {
  const month = new Date().getMonth();
  if (month >= 11 || month <= 2) return 'winter';
  if (month >= 3 && month <= 5) return 'summer';
  return 'monsoon';
};

// Assess project complexity based on requirements
const assessProjectComplexity = (requirements) => {
  const totalArea = requirements.reduce((sum, req) => sum + req.area, 0);
  const uniqueFinishes = new Set(requirements.map(req => req.specs.finish)).size;
  const hasExterior = requirements.some(req => req.type === 'exterior');
  const hasInterior = requirements.some(req => req.type === 'interior');
  
  if (uniqueFinishes > 3 || (hasExterior && hasInterior && totalArea > 50000)) {
    return 'complex';
  } else if (uniqueFinishes > 1 || (hasExterior && hasInterior)) {
    return 'moderate';
  }
  return 'simple';
};

// Assess urgency level based on deadline
const assessUrgencyLevel = (deadline) => {
  const deadlineDate = new Date(deadline);
  const today = new Date();
  const daysUntilDeadline = Math.ceil((deadlineDate - today) / (1000 * 60 * 60 * 24));
  
  if (daysUntilDeadline < 7) return 'emergency';
  if (daysUntilDeadline < 14) return 'urgent';
  if (daysUntilDeadline < 30) return 'expedited';
  return 'standard';
};

// Get waste factor based on surface type and complexity
const getWasteFactor = (surfaceType, complexity) => {
  const baseWaste = PRICING_RULES.waste_factors[surfaceType] || PRICING_RULES.waste_factors.interior;
  const complexityMultiplier = complexity === 'complex' ? 1.2 : complexity === 'moderate' ? 1.1 : 1.0;
  return baseWaste * complexityMultiplier;
};

// Calculate bulk discount based on quantity
const getBulkDiscount = (quantity, bulkDiscounts) => {
  let maxDiscount = 0;
  Object.entries(bulkDiscounts).forEach(([threshold, discount]) => {
    if (quantity >= parseInt(threshold)) {
      maxDiscount = Math.max(maxDiscount, discount);
    }
  });
  return maxDiscount;
};

// Calculate labor cost with complexity factors
const calculateLaborCost = (area, specs, complexity) => {
  let baseLaborCost = area * PRICING_RULES.labor_factors.base_rate_per_sqft;
  
  // Apply complexity multipliers
  if (specs.highCeilings) baseLaborCost *= PRICING_RULES.labor_factors.complexity_multipliers.high_ceilings;
  if (specs.difficultAccess) baseLaborCost *= PRICING_RULES.labor_factors.complexity_multipliers.difficult_access;
  if (specs.multipleCoats) baseLaborCost *= PRICING_RULES.labor_factors.complexity_multipliers.multiple_coats;
  if (specs.specialPrep) baseLaborCost *= PRICING_RULES.labor_factors.complexity_multipliers.special_prep;
  
  return baseLaborCost;
};

// Get project size category
const getProjectSize = (totalArea) => {
  if (totalArea < 10000) return 'small';
  if (totalArea < 50000) return 'medium';
  return 'large';
};

// Enhanced vendor scoring algorithm
const calculateEnhancedVendorScore = (price, reliability, leadTime, certificationCount) => {
  const priceScore = Math.max(0, 100 - (price / 1000)); // Lower price = higher score
  const reliabilityScore = reliability; // Direct reliability score
  const timeScore = Math.max(0, 100 - leadTime * 2); // Faster delivery = higher score
  const certificationScore = Math.min(100, certificationCount * 20); // More certifications = higher score
  
  return (priceScore * 0.3 + reliabilityScore * 0.3 + timeScore * 0.2 + certificationScore * 0.2);
};

// Legacy vendor scoring algorithm (kept for backward compatibility)
const calculateVendorScore = (price, reliability, leadTime) => {
  const priceScore = Math.max(0, 100 - (price / 1000)); // Lower price = higher score
  const reliabilityScore = reliability; // Direct reliability score
  const timeScore = Math.max(0, 100 - leadTime * 2); // Faster delivery = higher score
  
  return (priceScore * 0.4 + reliabilityScore * 0.4 + timeScore * 0.2);
};

// --- REACT APPLICATION COMPONENT ---
const statusMap = {
  IDLE: { text: "Ready to Start", icon: Search },
  UPLOADING: { text: "Processing PDF Upload", icon: Upload },
  SALES_SCANNING: { text: "1. Sales Agent: Extracting Requirements", icon: Search },
  TECHNICAL_MATCHING: { text: "2. Technical Agent: Multi-Vendor Analysis", icon: Sliders },
  PRICING_CALCULATING: { text: "3. Pricing Agent: Calculating Best Quote", icon: DollarSign },
  ORCHESTRATING: { text: "4. Orchestrator Agent: Finalizing Response", icon: Zap },
  COMPLETE: { text: "RFP Response Complete!", icon: CheckCircle },
  ERROR: { text: "Workflow Error", icon: Zap },
};

const App = () => {
  const [status, setStatus] = useState('IDLE');
  const [orchestratorOutput, setOrchestratorOutput] = useState(null);
  const [rfpText, setRfpText] = useState('');
  const [activeTab, setActiveTab] = useState('workflow');
  const [uploadedFile, setUploadedFile] = useState(null);
  const [foundRFPs, setFoundRFPs] = useState([]);
  const [isScanning, setIsScanning] = useState(false);
  const [useBackendExtraction, setUseBackendExtraction] = useState(false);
  const fileInputRef = useRef(null);
  
  // Default RFP text
  const DEFAULT_RFP = `REQUEST FOR PROPOSAL - Tender #PP-2024-001 | Commercial Building Paint Project
Deadline: 2024-12-15

PROJECT REQUIREMENTS:
We require painting services for a 50,000 sq ft commercial building complex.

Requirement A: Exterior Walls (25,000 sq ft)
- Finish Type: Weather-resistant Matt or Smooth finish
- Coverage: Minimum 120 sq ft per liter
- Durability: Must last minimum 10 years
- Application: Spray/Roller application

Requirement B: Interior Walls (25,000 sq ft)
- Finish Type: Silk or Satin finish preferred
- Coverage: Minimum 100 sq ft per liter  
- Durability: Minimum 8 years expected life
- Note: Include primer cost and labor charges of $5000.`;

  // Initialize with default text
  React.useEffect(() => {
    if (!rfpText) {
      setRfpText(DEFAULT_RFP);
    }
  }, [rfpText]);

  // File upload handler
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (file && file.type === 'application/pdf') {
      setUploadedFile(file);
      setStatus('UPLOADING');
      
      try {
        const extractedText = await extractTextFromPDF(file);
        setRfpText(extractedText);
        setStatus('IDLE');
      } catch (error) {
        console.error('PDF processing failed:', error);
        setStatus('ERROR');
      }
    }
  };

  // Web scan handler
  const handleScanWeb = async () => {
    setIsScanning(true);
    try {
      const results = await scanWebForRFPs();
      setFoundRFPs(results);
    } finally {
      setIsScanning(false);
    }
  };

  // Main orchestrator function
  const runOrchestrator = useCallback(async () => {
    if (status !== 'IDLE' && status !== 'COMPLETE') return;
    
    setStatus('SALES_SCANNING');
    setOrchestratorOutput(null);
    
    try {
      // Step 1: Sales Agent
      let salesResult;
      if (useBackendExtraction) {
        // Call backend to extract requirements
        const resp = await fetch('http://localhost:5001/api/extract-requirements', {
          method: 'POST', headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ text: rfpText })
        });
        const data = await resp.json();
        const requirements = Array.isArray(data.requirements) && data.requirements.length > 0
          ? data.requirements
          : (await salesAgent(rfpText, !!uploadedFile)).requirements;
        // Basic deadline guess preserved from local parser
        const parsed = parseRFPRequirements(rfpText);
        salesResult = { deadline: parsed.deadline, totalSqFt: parsed.totalSqFt, requirements };
      } else {
        salesResult = await salesAgent(rfpText, !!uploadedFile);
      }
      setStatus('TECHNICAL_MATCHING');
      
      // Step 2: Technical Agent (Multi-vendor)
      const technicalResult = await technicalAgent(salesResult);
      setStatus('PRICING_CALCULATING');
      
      // Step 3: Pricing Agent (Multi-vendor comparison)
      const finalResult = await pricingAgent(technicalResult);
      setStatus('ORCHESTRATING');
      
      // Step 4: Final compilation
      await delay(500);
      setOrchestratorOutput(finalResult);
      setStatus('COMPLETE');
    } catch (error) {
      console.error("Agent Workflow Failed:", error);
      setOrchestratorOutput({ error: error.message || "Unknown error during processing." });
      setStatus('ERROR');
    }
  }, [rfpText, uploadedFile, status]);

  const CurrentIcon = statusMap[status]?.icon;

  return (
    <div className="min-h-screen bg-gray-100 p-6 font-inter antialiased">
      <div className="max-w-7xl mx-auto">
        <header className="text-center py-8 bg-white rounded-xl shadow-lg mb-6">
          <h1 className="text-4xl font-black text-indigo-700">Paint RFP Automation System</h1>
          <p className="text-gray-500 mt-2">PDF Upload • Multi-Vendor Paint Comparison • Advanced Analytics</p>
        </header>

        {/* Tab Navigation */}
        <div className="bg-white rounded-xl shadow-lg mb-6">
          <div className="flex border-b">
            <TabButton 
              active={activeTab === 'workflow'} 
              onClick={() => setActiveTab('workflow')}
              icon={Zap}
              text="RFP Workflow"
            />
            <TabButton 
              active={activeTab === 'analytics'} 
              onClick={() => setActiveTab('analytics')}
              icon={BarChart3}
              text="Analytics Dashboard"
            />
            <TabButton 
              active={activeTab === 'vendors'} 
              onClick={() => setActiveTab('vendors')}
              icon={Users}
              text="Vendor Comparison"
            />
          </div>
        </div>

        {/* Tab Content */}
        {activeTab === 'workflow' && (
          <WorkflowTab 
            status={status}
            CurrentIcon={CurrentIcon}
            statusMap={statusMap}
            runOrchestrator={runOrchestrator}
            rfpText={rfpText}
            setRfpText={setRfpText}
            handleFileUpload={handleFileUpload}
            fileInputRef={fileInputRef}
            uploadedFile={uploadedFile}
            orchestratorOutput={orchestratorOutput}
            foundRFPs={foundRFPs}
            onScanWeb={handleScanWeb}
            isScanning={isScanning}
          />
        )}

        {activeTab === 'analytics' && (
          <AnalyticsTab />
        )}

        {activeTab === 'vendors' && orchestratorOutput && (
          <VendorComparisonTab orchestratorOutput={orchestratorOutput} />
        )}
      </div>
    </div>
  );
};

// --- TAB COMPONENTS ---
const TabButton = ({ active, onClick, icon: Icon, text }) => (
  <button
    onClick={onClick}
    className={`flex items-center px-6 py-4 font-semibold transition-all ${
      active 
        ? 'text-indigo-600 border-b-2 border-indigo-600 bg-indigo-50' 
        : 'text-gray-500 hover:text-gray-700'
    }`}
  >
    <Icon className="w-5 h-5 mr-2" />
    {text}
  </button>
);

