          >
            <Upload className="w-4 h-4 mr-2" />
            Upload PDF
          </button>
          <button
            onClick={onScanWeb}
            disabled={isScanning}
            className={`flex items-center px-4 py-2 rounded-lg text-white font-semibold transition-all ${
              isScanning ? 'bg-gray-400 cursor-not-allowed' : 'bg-teal-600 hover:bg-teal-700'
            }`}
          >
            <Search className="w-4 h-4 mr-2" />
            {isScanning ? 'Scanning...' : 'Scan Mock RFP URLs'}
