import React, { useState, useCallback } from 'react';
import { RefreshCw, Zap, Sliders, DollarSign, CheckCircle, Search } from 'lucide-react';

// --- MOCK DATA SIMULATING EXTERNAL DATABASES AND INPUTS ---
// 1. Mock Product Catalog (SKU Database)
const SKU_CATALOG = [
  { id: 'W001-A', name: 'Heavy Duty Power Cable 5kV', insulation: 'XLPE', conductor: 'Copper', rating: 5000, cost: 12.50 },
  { id: 'W002-B', name: 'Flexible Data Cable Low Voltage', insulation: 'PVC', conductor: 'Aluminum', rating: 600, cost: 1.80 },
  { id: 'W003-C', name: 'High Temp Fire Resistant Cable', insulation: 'Silicon', conductor: 'Copper', rating: 1000, cost: 4.25 },
  { id: 'W004-D', name: 'Standard Building Wire 12AWG', insulation: 'PVC', conductor: 'Copper', rating: 600, cost: 0.95 },
  { id: 'W005-E', name: 'Submarine Fiber Optic Bundle', insulation: 'PolyEthylene', conductor: 'Fiber', rating: 20000, cost: 85.00 },
];

// 2. Mock RFP Input (The tender document text)
const MOCK_RFP_PROMPT = `REQUEST FOR PROPOSAL - Tender #AP-2024-001 | Electrical Infrastructure Upgrade
Deadline: 2024-12-15

PROJECT REQUIREMENTS:
We require 5,000 meters of cable for a new industrial facility.

Requirement A: Heavy-Duty Cable (3000m)
- Insulation Material: XLPE or similar high-performance polymer.
- Conductor: Must be Copper.
- Voltage Rating: Must handle a minimum of 4kV.

Requirement B: Standard Cable (2000m)
- Insulation Material: PVC.
- Conductor: Must be Copper.
- Voltage Rating: Must handle up to 600V.
- Note: Must include a certification test fee of $1500.`;

// 3. Mock Pricing Rules
const PRICING_RULES = {
  markup_percentage: 0.20, // 20% markup on cost
  overhead_per_meter: 0.10, // $0.10 per meter
};

// --- AGENT SIMULATION LOGIC ---
// Helper function to simulate delay
const delay = (ms) => new Promise(resolve => setTimeout(resolve, ms));

// 1. Sales Agent Simulation
const salesAgent = async (rfpText) => {
  await delay(1000); // Simulating web scanning and initial summary
  console.log("Sales Agent: Tender picked and requirements summarized.");
  
  // MOCK LOGIC: Extracting key requirements from the text
  const requirements = [
    { id: 'A', length: 3000, specs: { insulation: 'XLPE', conductor: 'Copper', minRating: 4000 } },
    { id: 'B', length: 2000, specs: { insulation: 'PVC', conductor: 'Copper', maxRating: 600, testFee: 1500 } },
  ];
  
  return { deadline: '2024-12-15', totalMeters: 5000, requirements };
};

// 2. Technical Agent Simulation
const technicalAgent = async (salesOutput) => {
  await delay(2000); // Simulating complex SKU matching and comparison
  console.log("Technical Agent: Matching SKUs to requirements...");
  
  const matchedRequirements = salesOutput.requirements.map(req => {
    let bestMatch = null;
    let maxScore = -1;
    
    SKU_CATALOG.forEach(sku => {
      let score = 0;
      let reason = [];
      
      // Check Insulation
      if (sku.insulation.toLowerCase() === req.specs.insulation.toLowerCase()) {
        score += 40;
        reason.push("Insulation Match (40%)");
      }
      
      // Check Conductor
      if (sku.conductor.toLowerCase() === req.specs.conductor.toLowerCase()) {
        score += 40;
        reason.push("Conductor Match (40%)");
      }
      
      // Check Rating (Minimum requirement check)
      if (req.specs.minRating && sku.rating >= req.specs.minRating) {
        score += 20;
        reason.push("Voltage Rating Met (20%)");
      } else if (req.specs.maxRating && sku.rating <= req.specs.maxRating) {
        score += 20;
        reason.push("Voltage Rating Met (20%)");
      }
      
      if (score > maxScore) {
        maxScore = score;
        bestMatch = {
          skuId: sku.id,
          skuName: sku.name,
          skuCost: sku.cost,
          matchScore: maxScore,
          matchReason: reason.join(', '),
        };
      }
    });
    
    return { ...req, bestMatch };
  });
  
  return { deadline: salesOutput.deadline, matchedRequirements };
};

// 3. Pricing Agent Simulation
const pricingAgent = async (technicalOutput) => {
  await delay(1500); // Simulating cost lookup and calculation
  console.log("Pricing Agent: Calculating total cost and applying markup...");
  
  let subtotalCost = 0;
  let totalTestFees = 0;
  let totalLength = 0;
  
  technicalOutput.matchedRequirements.forEach(item => {
    const costPerMeter = item.bestMatch.skuCost;
    const length = item.length;
    const itemCost = costPerMeter * length;
    
    subtotalCost += itemCost;
    totalLength += length;
    
    if (item.specs.testFee) {
      totalTestFees += item.specs.testFee;
    }
  });
  
  const overheadCost = totalLength * PRICING_RULES.overhead_per_meter;
  const grossCost = subtotalCost + overheadCost + totalTestFees;
  const finalPrice = grossCost * (1 + PRICING_RULES.markup_percentage);
  
  return {
    ...technicalOutput,
    pricingDetails: {
      subtotalCost,
      overheadCost,
      totalTestFees,
      finalPrice: parseFloat(finalPrice.toFixed(2)),
      markupPercentage: PRICING_RULES.markup_percentage * 100,
      totalLength,
    }
  };
};

// --- REACT APPLICATION COMPONENT ---
const statusMap = {
  IDLE: { text: "Ready to Start", icon: Search },
  SALES_SCANNING: { text: "1. Sales Agent: Scanning Tenders & Summarizing", icon: Search },
  TECHNICAL_MATCHING: { text: "2. Technical Agent: Matching SKUs to Specs", icon: Sliders },
  PRICING_CALCULATING: { text: "3. Pricing Agent: Calculating Final Quote", icon: DollarSign },
  ORCHESTRATING: { text: "4. Orchestrator Agent: Compiling Final Response", icon: Zap },
  COMPLETE: { text: "RFP Response Complete!", icon: CheckCircle },
  ERROR: { text: "Workflow Error", icon: Zap },
};

const App = () => {
  const [status, setStatus] = useState('IDLE');
  const [orchestratorOutput, setOrchestratorOutput] = useState(null);
  
  // 4. Main Orchestrator Agent Function
  const runOrchestrator = useCallback(async () => {
    if (status !== 'IDLE' && status !== 'COMPLETE') return;
    
    setStatus('SALES_SCANNING');
    setOrchestratorOutput(null);
    
    try {
      // Step 1: Sales Agent
      const salesResult = await salesAgent(MOCK_RFP_PROMPT);
      setStatus('TECHNICAL_MATCHING');
      
      // Step 2: Technical Agent
      const technicalResult = await technicalAgent(salesResult);
      setStatus('PRICING_CALCULATING');
      
      // Step 3: Pricing Agent
      const finalResult = await pricingAgent(technicalResult);
      setStatus('ORCHESTRATING');
      
      // Step 4: Orchestrator final compilation
      await delay(500);
      setOrchestratorOutput(finalResult);
      setStatus('COMPLETE');
    } catch (error) {
      console.error("Agent Workflow Failed:", error);
      setOrchestratorOutput({ error: error.message || "Unknown error during processing." });
      setStatus('ERROR');
    }
  }, [status]);
  
  const CurrentIcon = statusMap[status]?.icon;
  
  const renderResult = () => {
    if (!orchestratorOutput) return null;
    
    const { matchedRequirements, pricingDetails } = orchestratorOutput;
    
    if (orchestratorOutput.error) {
      return (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-xl mt-6">
          <p className="font-semibold">Error:</p>
          <p>{orchestratorOutput.error}</p>
        </div>
      );
    }
    
    return (
      <div className="space-y-8 mt-8 p-6 bg-white shadow-2xl rounded-2xl border-t-4 border-indigo-500">
        <h2 className="text-3xl font-extrabold text-gray-800 flex items-center">
          <CheckCircle className="w-7 h-7 text-green-500 mr-3" />
          Final RFP Response Generated
        </h2>
        
        {/* Pricing Summary */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
          <StatCard title="Total Length" value={`${pricingDetails.totalLength}m`} color="bg-indigo-50" />
          <StatCard title="Target Deadline" value={orchestratorOutput.deadline} color="bg-indigo-50" />
          <StatCard title="Overhead & Fees" value={`$${(pricingDetails.overheadCost + pricingDetails.totalTestFees).toFixed(2)}`} color="bg-indigo-50" />
          <StatCard title="FINAL QUOTE" value={`$${pricingDetails.finalPrice.toLocaleString()}`} color="bg-green-100 text-green-700" isFinal={true} />
        </div>
        
        {/* Product Matches */}
        <h3 className="text-2xl font-semibold text-gray-700 border-b pb-2 mb-4">Technical Match Details</h3>
        <div className="space-y-4">
          {matchedRequirements.map((req, index) => (
            <div key={req.id} className="p-4 border border-gray-200 rounded-xl bg-gray-50 shadow-sm">
              <p className="text-lg font-bold text-indigo-600 mb-2">Requirement {req.id}: {req.length}m</p>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <DetailItem label="Matched SKU" value={req.bestMatch.skuId} secondary={req.bestMatch.skuName} />
                <DetailItem label="Match Score" value={`${req.bestMatch.matchScore}%`} secondary="Higher is better" score={req.bestMatch.matchScore} />
                <DetailItem label="Technical Reason" value={req.bestMatch.matchReason} secondary="The AI's justification" isReason={true} />
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };  
return (
    <div className="min-h-screen bg-gray-100 p-6 font-inter antialiased">
      <div className="max-w-4xl mx-auto">
        <header className="text-center py-8 bg-white rounded-xl shadow-lg mb-6">
          <h1 className="text-4xl font-black text-indigo-700">Agentic RFP Automation</h1>
          <p className="text-gray-500 mt-2">Simulating the Sales, Technical, and Pricing Agent Workflow</p>
        </header>
        
        {/* Control Panel */}
        <div className="bg-white p-6 rounded-xl shadow-lg flex flex-col md:flex-row justify-between items-center mb-6">
          <div className="flex items-center space-x-3 mb-4 md:mb-0">
            <CurrentIcon className={`w-8 h-8 ${
              status === 'COMPLETE' ? 'text-green-500' : 
              status === 'IDLE' ? 'text-gray-400' : 
              'text-indigo-500 animate-spin-slow'
            }`} />
            <span className={`text-xl font-semibold ${
              status === 'COMPLETE' ? 'text-green-700' : 'text-gray-800'
            }`}>
              Current Status: {statusMap[status]?.text}
            </span>
          </div>
          
          <button
            onClick={runOrchestrator}
            disabled={status !== 'IDLE' && status !== 'COMPLETE' && status !== 'ERROR'}
            className={`flex items-center px-6 py-3 rounded-full text-white font-bold transition-all duration-300 transform ${
              status === 'IDLE' || status === 'COMPLETE' || status === 'ERROR'
                ? 'bg-indigo-600 hover:bg-indigo-700 shadow-md hover:shadow-lg active:scale-95'
                : 'bg-gray-400 cursor-not-allowed opacity-75'
            }`}
          >
            <Zap className="w-5 h-5 mr-2" />
            {status === 'COMPLETE' ? 'Run Again' : 'Start RFP Process'}
          </button>
        </div>
        
        {/* Input & Output */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* RFP Input */}
          <div className="bg-white p-6 rounded-xl shadow-lg h-96 overflow-y-auto">
            <h2 className="text-xl font-bold text-gray-800 mb-4 border-b pb-2">RFP Input (Sales Agent Start)</h2>
            <pre className="text-sm bg-gray-50 p-3 rounded-lg border border-gray-200 whitespace-pre-wrap">
              {MOCK_RFP_PROMPT}
            </pre>
          </div>
          
          {/* Workflow Visualization */}
          <div className="bg-white p-6 rounded-xl shadow-lg flex flex-col justify-between h-96">
            <h2 className="text-xl font-bold text-gray-800 mb-4 border-b pb-2">Agent Workflow Timeline</h2>
            <WorkflowStep status={status} stepKey="SALES_SCANNING" title="Sales Agent (Find & Filter)" description="Identifies tender and extracts structured requirements." />
            <WorkflowStep status={status} stepKey="TECHNICAL_MATCHING" title="Technical Agent (Match & Score)" description="Compares requirements against 5 SKUs and scores the best fit." />
            <WorkflowStep status={status} stepKey="PRICING_CALCULATING" title="Pricing Agent (Cost & Quote)" description="Looks up unit costs, adds fees, overhead, and markup." />
            <WorkflowStep status={status} stepKey="ORCHESTRATING" title="Orchestrator (Finalize Response)" description="Aggregates data into a single, comprehensive tender response." />
          </div>
        </div>
        
        {/* Final Output Panel */}
        {renderResult()}
      </div>
    </div>
  );
};

// --- Sub-Components for UI Clarity ---
const WorkflowStep = ({ status, stepKey, title, description }) => {
  const isCurrent = status === stepKey;
  const isCompleted = ['TECHNICAL_MATCHING', 'PRICING_CALCULATING', 'ORCHESTRATING', 'COMPLETE'].includes(status) &&
    (statusMap[stepKey].icon !== Zap || status === 'COMPLETE'); // Simpler completed check
  
  let dotColor = 'bg-gray-300';
  if (isCompleted) dotColor = 'bg-green-500';
  if (isCurrent) dotColor = 'bg-indigo-500 animate-pulse';
  
  return (
    <div className="flex items-start mb-4">
      <div className={`w-3 h-3 rounded-full mt-1.5 mr-4 ${dotColor}`}></div>
      <div>
        <h4 className={`text-lg font-semibold ${
          isCurrent ? 'text-indigo-600' : 
          isCompleted ? 'text-gray-700' : 'text-gray-500'
        }`}>
          {title}
        </h4>
        <p className="text-sm text-gray-500">{description}</p>
      </div>
    </div>
  );
};

const StatCard = ({ title, value, color, isFinal = false }) => (
  <div className={`p-4 rounded-xl ${color} shadow-sm transition-all duration-300 ${
    isFinal ? 'border-2 border-green-400 scale-105' : 'border border-gray-200'
  }`}>
    <p className={`text-sm font-medium ${isFinal ? 'text-green-600' : 'text-gray-500'}`}>{title}</p>
    <p className={`text-xl font-bold mt-1 ${isFinal ? 'text-green-800' : 'text-gray-900'}`}>{value}</p>
  </div>
);

const DetailItem = ({ label, value, secondary, score = null, isReason = false }) => {
  let scoreColor = 'text-gray-800';
  if (score !== null) {
    if (score >= 80) scoreColor = 'text-green-600 font-extrabold';
    else if (score >= 40) scoreColor = 'text-yellow-600';
    else scoreColor = 'text-red-600';
  }
  
  return (
    <div className="flex flex-col">
      <span className="text-xs font-medium text-gray-500">{label}</span>
      <span className={`text-base mt-1 ${isReason ? 'text-sm' : scoreColor}`}>{value}</span>
      <span className="text-xs text-gray-400">{secondary}</span>
    </div>
  );
};

export default App;
